export async function test_create_product_category_and_validate_hierarchy_and_uniqueness(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a root category (no parent_id)
  const rootName = `RootCategory_${Math.random().toString(36).substring(2, 8)}`;
  const rootInput: IProductCategory.ICreate = { name: rootName };
  const rootCategory = await api.functional.productCatalog.productCategories.post(connection, { body: rootInput });
  typia.assert<typeof rootCategory>(rootCategory);
  TestValidator.predicate("root: no parent_id")(() => rootCategory.parent_id == null);
  TestValidator.equals("root: name matches")<string>(rootName)(rootCategory.name);

  // 2. Create a child category under root
  const childName = `ChildCategory_${Math.random().toString(36).substring(2, 8)}`;
  const childInput: IProductCategory.ICreate = { name: childName, parent_id: rootCategory.id };
  const childCategory = await api.functional.productCatalog.productCategories.post(connection, { body: childInput });
  typia.assert<typeof childCategory>(childCategory);
  TestValidator.equals("child: parent_id matches root id")<string>(rootCategory.id)(childCategory.parent_id!);
  TestValidator.equals("child: name matches")<string>(childName)(childCategory.name);

  // 3. Try to create a duplicate category (same name under same parent) â€” should fail
