  // 5. Attempt to create child with non-existent parent â€” should fail
  const nonExistentParentInput: IProductCategory.ICreate = {
    name: "InvalidParentChild",
    parent_id: "00000000-0000-0000-0000-000000000000", // Clearly invalid UUID
  };
  await TestValidator.error("should reject non-existent parent_id")(
    () => api.functional.productCatalog.productCategories.post(connection, { body: nonExistentParentInput })
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IProductCategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCategory";
import typia, { tags } from "typia";

export async function test_update_product_category_and_validate_relationships(connection: api.IConnection): Promise<void> {
  // Step 1: Create a root category
  const rootName = `Root_${Date.now()}`;
  const rootCategory: IProductCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: { name: rootName },
  });
  typia.assert<IProductCategory>(rootCategory);
  TestValidator.equals("category name should match")(rootCategory.name)(rootName);
  TestValidator.equals("category should be root")(rootCategory.parent_id)(null);

  // Step 2: Update its name
  const updatedName = rootName + "_Updated";
  const updatedRoot: IProductCategory = await api.functional.productCatalog.productCategories.putById(connection, {
    id: rootCategory.id,
    body: { name: updatedName },
  });
  typia.assert<IProductCategory>(updatedRoot);
  TestValidator.equals("updated name")(updatedRoot.name)(updatedName);
  TestValidator.equals("id remains")(updatedRoot.id)(rootCategory.id);

  // Step 3: Create a child category
  const childName = `Child_${Date.now()}`;
  const childCategory: IProductCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: { name: childName, parent_id: rootCategory.id },
  });
  typia.assert<IProductCategory>(childCategory);
  TestValidator.equals("child's parent_id")(childCategory.parent_id)(rootCategory.id);

  // Step 4: Move child to root (parent_id: null)
  const childRoot: IProductCategory = await api.functional.productCatalog.productCategories.putById(connection, {
    id: childCategory.id,
    body: { parent_id: null },
  });
  typia.assert<IProductCategory>(childRoot);
  TestValidator.equals("child is now root")(childRoot.parent_id)(null);

  // Step 5: Try updating non-existent category
  const randomId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("update non-existent category fails")(404)(async () => {
    await api.functional.productCatalog.productCategories.putById(connection, {
      id: randomId,
      body: { name: "will-not-work" },
    });
  });

  // Step 6: Create another category for conflict
  const conflictName = `Conflict_${Date.now()}`;
  const anotherCategory: IProductCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: { name: conflictName },
  });
  typia.assert<IProductCategory>(anotherCategory);
  // Now try to rename child to same name (should fail)
  await TestValidator.httpError("category name conflict validation")(400, 409)(async () => {
    await api.functional.productCatalog.productCategories.putById(connection, {
      id: childCategory.id,
      body: { name: conflictName },
    });
  });

  // Step 7: Set parent_id to invalid (non-existent)
