export async function test_list_product_options_with_filtering_and_pagination(
  connection: api.IConnection
): Promise<void> {
  // Create distinct product options for search scenarios
  const product_id_1 = typia.random<IProductOption["product_id"]>();
  const product_id_2 = typia.random<IProductOption["product_id"]>();

  const option1 = await api.functional.productCatalog.productOptions.post(connection, {
