      product_id: product_id_1,
      option_type: "size",
      value: "Large",
    },
  });
  const option3 = await api.functional.productCatalog.productOptions.post(connection, {
    body: {
      product_id: product_id_2,
      option_type: "color",
      value: "Blue",
    },
  });

  // 1. Basic listing (no filters): should contain all created options
  const resp_all = await api.functional.productCatalog.productOptions.patch(connection, {
    body: {},
  });
  typia.assert<IPageIProductOption>(resp_all);
  TestValidator.predicate("all created options are listed")(
    () => [option1.id, option2.id, option3.id].every(id => resp_all.data.some(opt => opt.id === id))
  );

  // 2. Filter by product_id: only product_id_1 options
  const resp_prod1 = await api.functional.productCatalog.productOptions.patch(connection, {
    body: { product_id: product_id_1 },
  });
  typia.assert<IPageIProductOption>(resp_prod1);
  TestValidator.predicate("filter by product_id")(
    () => resp_prod1.data.every(opt => opt.product_id === product_id_1)
  );

  // 3. Filter by option_type: 'color'
  const resp_color = await api.functional.productCatalog.productOptions.patch(connection, {
    body: { option_type: "color" },
  });
  typia.assert<IPageIProductOption>(resp_color);
  TestValidator.predicate("filter by option_type=color")(
    () => resp_color.data.every(opt => opt.option_type === "color")
  );

  // 4. Filter by value (exact)
  const resp_red = await api.functional.productCatalog.productOptions.patch(connection, {
    body: { value: "Red" },
  });
  typia.assert<IPageIProductOption>(resp_red);
  TestValidator.predicate("exact value match: Red")(
    () => resp_red.data.every(opt => opt.value === "Red")
