  // 5. Filter by value (partial - assume substring match supported)
  const resp_large_partial = await api.functional.productCatalog.productOptions.patch(connection, {
    body: { value: "Lar" },
  });
  typia.assert<IPageIProductOption>(resp_large_partial);
