    () => resp_limit2_page1.data.length <= 2 && resp_limit2_page1.pagination.current === 1
  );

  // 7. Pagination: page boundary (page far beyond existing data)
  const resp_page100 = await api.functional.productCatalog.productOptions.patch(connection, {
    body: { limit: 2, page: 100 },
  });
  typia.assert<IPageIProductOption>(resp_page100);
  TestValidator.equals("empty data for non-existent page")<IProductOption[]>([])(resp_page100.data);

