import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { IRefund } from "@ORGANIZATION/PROJECT-api/lib/structures/IRefund";

export async function test_update_refund_with_valid_and_invalid_id(connection: api.IConnection): Promise<void> {
  // 1. Create initial refund
  const createInput: IRefund.ICreate = typia.random<IRefund.ICreate>();
  const created: IRefund = await api.functional.paymentsDiscounts.refunds.post(connection, {
    body: createInput,
  });
  typia.assert<IRefund>(created);

  // 2. Update - prepare valid change for at least two fields
  const updatePayload: IRefund.IUpdate = {
    amount: (created.amount ?? 100) + 50,
    status: created.status === "requested" ? "processed" : "requested",
    reason: "Updated for test purpose",
  };
  const updated: IRefund = await api.functional.paymentsDiscounts.refunds.putById(connection, {
    id: created.id,
    body: updatePayload,
  });
  typia.assert<IRefund>(updated);
  // Ensure that updated fields are changed, immutable ones fixed
  TestValidator.equals("update.amount")(updatePayload.amount)(updated.amount);
  TestValidator.equals("update.status")(updatePayload.status)(updated.status);
  TestValidator.equals("update.reason")(updatePayload.reason)(updated.reason);
  TestValidator.equals("update.payment_id")(created.payment_id)(updated.payment_id);
  TestValidator.equals("update.transaction_id")(created.transaction_id)(updated.transaction_id);

  // 3. Attempt update for a non-existent id (expect 404)
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("putById with non-existent id")(404)(async () => {
    await api.functional.paymentsDiscounts.refunds.putById(connection, {
      id: nonExistentId,
      body: updatePayload,
    });
  });

  // 4. Concurrent updates
  const concurrentPayload1: IRefund.IUpdate = {
    status: "completed",
    reason: "Concurrent update 1",
  };
  const concurrentPayload2: IRefund.IUpdate = {
    amount: (created.amount ?? 100) + 200,
    reason: "Concurrent update 2",
  };

  const [res1, res2] = await Promise.all([
    api.functional.paymentsDiscounts.refunds.putById(connection, {
      id: created.id,
      body: concurrentPayload1,
    }),
    api.functional.paymentsDiscounts.refunds.putById(connection, {
      id: created.id,
      body: concurrentPayload2,
    }),
  ]);
  typia.assert<IRefund>(res1);
  typia.assert<IRefund>(res2);
  // The final state should match one of the concurrent updates' intent
  const latest = await api.functional.paymentsDiscounts.refunds.putById(connection, {
    id: created.id,
    body: {}, // no change, just get latest
  });
  typia.assert<IRefund>(latest);
  // Accept if latest matches either concurrentPayload1 or concurrentPayload2 (for updated allowed fields)
  const matchesPayload1 = (latest.status === concurrentPayload1.status && latest.reason === concurrentPayload1.reason);
  const matchesPayload2 = (latest.amount === concurrentPayload2.amount && latest.reason === concurrentPayload2.reason);
  TestValidator.predicate("concurrent update result is consistent")(() => matchesPayload1 || matchesPayload2);
}

