import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPaymentAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPaymentAudit";
import { IPageIPaymentAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPaymentAudit";
import typia from "typia";

export async function test_list_payment_audits_with_various_filters(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create known audit record
  const now = new Date();
  const timestamp = now.toISOString();
  const uniqueEntityId =
    "00000000-" + Math.floor(Math.random() * 1e9).toString().padStart(9, "0") + "-0000-0000-0000-000000000000";
  const auditCreate: IPaymentAudit.ICreate = {
    entity_id: uniqueEntityId,
    entity_type: "payment",
    action: "create",
    user_id: null,
    old_value: null,
    new_value: "{\"status\":\"active\"}",
    timestamp,
  };
  const created = await api.functional.paymentsDiscounts.paymentAudits.post(connection, {
    body: auditCreate,
  });
  typia.assert<IPaymentAudit>(created);

  // Step 2: List all audits, no filter
  const listAll = await api.functional.paymentsDiscounts.paymentAudits.patch(connection, {
    body: {},
  });
  typia.assert<IPageIPaymentAudit>(listAll);
  TestValidator.predicate("Pagination metadata present")(() => !!listAll.pagination && typeof listAll.pagination.current === "number");
  TestValidator.predicate("At least 1 record exists")(() => listAll.data.length > 0);
  TestValidator.predicate("Created audit record is in the list")(() =>
    listAll.data.some(audit => audit.id === created.id)
  );

  // Step 3: List with entity_id filter (should match exactly one)
  const listByEntity = await api.functional.paymentsDiscounts.paymentAudits.patch(connection, {
    body: { entity_id: uniqueEntityId },
  });
  typia.assert<IPageIPaymentAudit>(listByEntity);
  TestValidator.predicate("Entity id filter returns exactly one record")(
    () => listByEntity.data.length === 1
  );
  TestValidator.predicate("Returned record has correct entity_id")(
    () => listByEntity.data[0].entity_id === uniqueEntityId
  );

  // Step 4: List with inclusive date range filter (should find the record)
  const listByDate = await api.functional.paymentsDiscounts.paymentAudits.patch(connection, {
    body: {
      timestamp_from: new Date(now.getTime() - 1000).toISOString(),
      timestamp_to: new Date(now.getTime() + 1000 * 60 * 10).toISOString(),
    },
  });
  typia.assert<IPageIPaymentAudit>(listByDate);
  TestValidator.predicate("Date range includes record")(
    () => listByDate.data.some(audit => audit.id === created.id)
  );

  // Step 5: List with exclusive date range (before creation, should find nothing)
  const listByDateNone = await api.functional.paymentsDiscounts.paymentAudits.patch(connection, {
    body: {
      timestamp_from: new Date(now.getTime() - 1000 * 60 * 20).toISOString(),
      timestamp_to: new Date(now.getTime() - 1000 * 60 * 10).toISOString(),
    },
  });
  typia.assert<IPageIPaymentAudit>(listByDateNone);
  TestValidator.predicate("No records for exclusive past date range")(
    () => listByDateNone.data.length === 0
  );

  // Step 6: List by non-existent entity_id (should return empty array)
  const listNonexistent = await api.functional.paymentsDiscounts.paymentAudits.patch(connection, {
    body: { entity_id: "ffffffff-ffff-ffff-ffff-ffffffffffff" },
  });
  typia.assert<IPageIPaymentAudit>(listNonexistent);
  TestValidator.predicate("No records for non-existent entity_id")(
    () => listNonexistent.data.length === 0
  );

  // Step 7: Always validate pagination metadata structure in each listing
  const checkPagination = (page: IPageIPaymentAudit) => {
    return (
      typeof page.pagination.current === "number" &&
      typeof page.pagination.limit === "number" &&
      typeof page.pagination.records === "number" &&
      typeof page.pagination.pages === "number"
    );
  };
  TestValidator.predicate("Pagination metadata valid (all)")(() =>
    checkPagination(listAll) &&
    checkPagination(listByEntity) &&
    checkPagination(listByDate) &&
    checkPagination(listByDateNone) &&
    checkPagination(listNonexistent)
  );
}

