import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IMessageThread } from "@ORGANIZATION/PROJECT-api/lib/structures/IMessageThread";
import { IPageIMessageThread } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIMessageThread";

/**
 * Test listing message threads with filters and pagination
 */
export async function test_list_message_threads_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a message thread to ensure data exists
  const threadInput: IMessageThread.ICreate = {
    subject: "TestSubject_Filtered",
    archived: false,
  };
  const created = await api.functional.communicationReview.messageThreads.post(connection, { body: threadInput });
  typia.assert<IMessageThread>(created);

  // 2. Default pagination (no filter)
  const resDefault = await api.functional.communicationReview.messageThreads.patch(connection, { body: {} });
  typia.assert<IPageIMessageThread>(resDefault);
  TestValidator.predicate("at least one thread exists")(() => resDefault.data.length >= 1);
  TestValidator.predicate("pagination metadata")(() => typeof resDefault.pagination.current === "number" && typeof resDefault.pagination.limit === "number");

  // 3. Filter by subject
  const resSubject = await api.functional.communicationReview.messageThreads.patch(connection, { body: { subject: threadInput.subject } });
  typia.assert<IPageIMessageThread>(resSubject);
  TestValidator.predicate("subject filter matches")(() =>
    resSubject.data.some((t) => t.subject === threadInput.subject),
  );

  // 4. Filter by created_by_user_id
  if (created.created_by_user_id) {
    const resCreator = await api.functional.communicationReview.messageThreads.patch(connection, { body: { created_by_user_id: created.created_by_user_id } });
    typia.assert<IPageIMessageThread>(resCreator);
    TestValidator.predicate("creator filter matches")(() =>
      resCreator.data.every((t) => t.created_by_user_id === created.created_by_user_id),
    );
  }

  // 5. Filter by date range (created_from/created_to)
  const now = new Date().toISOString();
  const resBefore = await api.functional.communicationReview.messageThreads.patch(connection, { body: { created_to: created.created_at } });
  typia.assert<IPageIMessageThread>(resBefore);

  const resAfter = await api.functional.communicationReview.messageThreads.patch(connection, { body: { created_from: now } });
  typia.assert<IPageIMessageThread>(resAfter);
  TestValidator.predicate("after current time yields empty")(() => resAfter.data.length === 0);

  // 6. Archived status filter
  const resArchived = await api.functional.communicationReview.messageThreads.patch(connection, { body: { archived: true } });
  typia.assert<IPageIMessageThread>(resArchived);

  // Optionally, create an archived thread & confirm filtered result
  const archivedThreadInput: IMessageThread.ICreate = { subject: "ArchivedThread", archived: true };
  const archivedThread = await api.functional.communicationReview.messageThreads.post(connection, { body: archivedThreadInput });
  typia.assert<IMessageThread>(archivedThread);
  const resArchivedFound = await api.functional.communicationReview.messageThreads.patch(connection, { body: { archived: true, subject: archivedThreadInput.subject } });
  typia.assert<IPageIMessageThread>(resArchivedFound);
  TestValidator.predicate("archived thread found")(() =>
    resArchivedFound.data.some((t) => t.subject === archivedThreadInput.subject && t.archived === true),
  );

  // 7. Sort by created_at ascending and descending
  const resSortedAsc = await api.functional.communicationReview.messageThreads.patch(connection, { body: { sort_field: "created_at", sort_order: "asc" } });
  const resSortedDesc = await api.functional.communicationReview.messageThreads.patch(connection, { body: { sort_field: "created_at", sort_order: "desc" } });
  typia.assert<IPageIMessageThread>(resSortedAsc);
  typia.assert<IPageIMessageThread>(resSortedDesc);
  if (resSortedAsc.data.length >= 2 && resSortedDesc.data.length >= 2) {
    TestValidator.predicate("sort order asc/desc differs")(() =>
      resSortedAsc.data[0].id !== resSortedDesc.data[0].id,
    );
  }

  // 8. Pagination: limit and page
  const resLimit1 = await api.functional.communicationReview.messageThreads.patch(connection, { body: { limit: 1 } });
  typia.assert<IPageIMessageThread>(resLimit1);
  TestValidator.predicate("limit 1")(() => resLimit1.data.length <= 1);

  if ((resDefault.pagination.pages ?? 0) > 1) {
    const resSecondPage = await api.functional.communicationReview.messageThreads.patch(connection, { body: { page: 2, limit: resDefault.pagination.limit } });
    typia.assert<IPageIMessageThread>(resSecondPage);
    TestValidator.predicate("pagination page 2 valid")(() => resSecondPage.pagination.current === 2);
  }

  // 9. Empty result filter with bogus subject
  const resEmpty = await api.functional.communicationReview.messageThreads.patch(connection, { body: { subject: "NO_MATCHING_THREAD_SUBJECT_BOGUS" } });
  typia.assert<IPageIMessageThread>(resEmpty);
  TestValidator.predicate("empty search yields no threads")(() => resEmpty.data.length === 0);

  // 10. Extreme/invalid filter values
  const resExtreme1 = await api.functional.communicationReview.messageThreads.patch(connection, { body: { limit: 999 } });
  typia.assert<IPageIMessageThread>(resExtreme1);
  const resExtreme2 = await api.functional.communicationReview.messageThreads.patch(connection, { body: { page: 9999 } });
  typia.assert<IPageIMessageThread>(resExtreme2);

  // 11. Response type structure in all queries
  [resDefault, resSubject, resArchived, resSortedAsc, resSortedDesc, resLimit1, resEmpty, resExtreme1, resExtreme2].forEach((res) => {
    typia.assert<IPageIMessageThread>(res);
    TestValidator.predicate("pagination struct valid")(() => typeof res.pagination.current === "number" && typeof res.pagination.records === "number" && typeof res.pagination.pages === "number");
  });
}

