  return msg;
}

export async function test_list_messages_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // Setup test messages
  const thread1 = crypto.randomUUID();
  const thread2 = crypto.randomUUID();
  const userA = crypto.randomUUID();
  const userB = crypto.randomUUID();
  const now = new Date();
  const yesterday = new Date(now.getTime() - 24 * 3600 * 1000);
  const twoDaysAgo = new Date(now.getTime() - 2 * 24 * 3600 * 1000);

  // Create diverse test messages
  const msg1 = await createMessage(connection, {
    thread_id: thread1,
    content: "Hello world A",
    message_type: "user",
  });
  const msg2 = await createMessage(connection, {
    thread_id: thread1,
    content: "Filter by me",
    message_type: "user",
  });
  const msg3 = await createMessage(connection, {
    thread_id: thread2,
    content: "System msg",
    message_type: "system",
  });
  // Simulate different sender IDs by patching after creation (typia.random does not allow custom sender).
  // In real-life, endpoints would determine sender via authentication. For this test, we proceed with generated senders.

  // 1. List all messages, no filters
  const allRes = await api.functional.communicationReview.messages.patch(connection, {
    body: {},
  });
  typia.assert<IPageIMessage>(allRes);
  TestValidator.predicate("all messages count >= 3")(
    allRes.data.length >= 3,
  );

  // 2. Filter by thread_id
  const thread1Res = await api.functional.communicationReview.messages.patch(connection, {
    body: { thread_id: thread1 },
  });
  typia.assert<IPageIMessage>(thread1Res);
  TestValidator.predicate("all thread1 messages have correct thread_id")(
    thread1Res.data.every((m) => m.thread_id === thread1),
  );

  // 3. Filter by message_type
  const systemRes = await api.functional.communicationReview.messages.patch(connection, {
    body: { message_type: "system" },
  });
  typia.assert<IPageIMessage>(systemRes);
  TestValidator.predicate("all system messages")(
    systemRes.data.every((m) => m.message_type === "system"),
  );

  // 4. Filter by date range
  const fromStr = twoDaysAgo.toISOString();
  const toStr = now.toISOString();
  const dateRes = await api.functional.communicationReview.messages.patch(connection, {
    body: { sent_from: fromStr, sent_to: toStr },
  });
  typia.assert<IPageIMessage>(dateRes);
  TestValidator.predicate("messages are within date range")(
    dateRes.data.every((m) => new Date(m.sent_at) >= twoDaysAgo && new Date(m.sent_at) <= now),
  );

  // 5. Paging test (limit)
  const pagedRes = await api.functional.communicationReview.messages.patch(connection, {
    body: { limit: 2 },
  });
  typia.assert<IPageIMessage>(pagedRes);
  TestValidator.predicate("paged result <= limit")(
    pagedRes.data.length <= 2,
  );

  // 6. Content search
  const contentRes = await api.functional.communicationReview.messages.patch(connection, {
    body: { content: "Filter by me" },
  });
  typia.assert<IPageIMessage>(contentRes);
  TestValidator.predicate("content search")(
    contentRes.data.every((m) => m.content.includes("Filter by me")),
  );

  // 7. Archived filter (create archived message)
  const archivedId = crypto.randomUUID();
  const archivedMsg = await createMessage(connection, {
    thread_id: archivedId,
    content: "to archive",
    message_type: "user",
  });
  // Simulate archived (artificial for test, API may differ)
  // Skipped: as no API method to set archived=true
  const archRes = await api.functional.communicationReview.messages.patch(connection, {
    body: { thread_id: archivedId, archived: false },
  });
  typia.assert<IPageIMessage>(archRes);
  TestValidator.predicate("archived filter")(
    archRes.data.every((m) => m.archived === false),
  );

  // 8. Edge case: filter that matches nothing
  const noRes = await api.functional.communicationReview.messages.patch(connection, {
    body: { content: "NO_MATCH_RANDOM_12345" },
  });
  typia.assert<IPageIMessage>(noRes);
  TestValidator.equals("empty result")([] as IMessage[])(noRes.data);
}

