import api from "@ORGANIZATION/PROJECT-api";
import { IMessage } from "@ORGANIZATION/PROJECT-api/lib/structures/IMessage";
import typia, { tags } from "typia";

export async function test_update_message_valid_invalid_and_not_found(
  connection: api.IConnection,
): Promise<void> {
  // 1. Preparation: Create a message to update
  const created: IMessage = await api.functional.communicationReview.messages.post(connection, {
    body: {
      thread_id: typia.random<string & tags.Format<"uuid">>(),
      content: "Initial content",
      message_type: "user",
      parent_message_id: null,
    },
  });
  typia.assert<IMessage>(created);

  // 2. Valid update: Change content & message_type
  const updateInput: IMessage.IUpdate = {
    content: "Updated content",
    message_type: "system",
    archived: true,
  };

  const updated = await api.functional.communicationReview.messages.putById(connection, {
    id: created.id,
    body: updateInput,
  });
  typia.assert<IMessage>(updated);
  TestValidator.equals("id unchanged")(created.id)(updated.id);
  TestValidator.equals("thread_id unchanged")(created.thread_id)(updated.thread_id);
  TestValidator.equals("sender unchanged")(created.sender_user_id)(updated.sender_user_id);
  TestValidator.equals("content updated")(updateInput.content!)(updated.content);
  TestValidator.equals("message_type updated")(updateInput.message_type!)(updated.message_type);
  TestValidator.equals("archived flag updated")(updateInput.archived!)(updated.archived);
  TestValidator.equals("parent_message_id unchanged")(
    created.parent_message_id ?? null
  )(updated.parent_message_id ?? null);

  // 3. Invalid update: Try to change forbidden/immutable fields & invalid input
  // Try to update id/thread_id/sender_user_id/sent_at (should be ignored)
  // These fields are not allowed in IMessage.IUpdate, but forcibly adding them for test
  const invalidUpdate: any = {
    id: typia.random<string & tags.Format<"uuid">>(),
    thread_id: typia.random<string & tags.Format<"uuid">>(),
    sender_user_id: typia.random<string & tags.Format<"uuid">>(),
    sent_at: new Date().toISOString(),
    content: "Should not work",
  };
  await TestValidator.error("Should reject update with forbidden fields")(
    async () =>
      await api.functional.communicationReview.messages.putById(connection, {
        id: created.id,
        body: invalidUpdate,
      })
  );

  // Try with invalid content (empty string)
  const badContent: IMessage.IUpdate = { content: "" };
  await TestValidator.error("Should reject update with empty content")(
    async () =>
      await api.functional.communicationReview.messages.putById(connection, {
        id: created.id,
        body: badContent,
      })
  );

  // 4. Not-Found update: invalid ID should yield 404
  const randomId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("Update non-existent message should 404")(404)(
    async () =>
      await api.functional.communicationReview.messages.putById(connection, {
        id: randomId,
        body: updateInput,
      })
  );
