import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { INotification } from "@ORGANIZATION/PROJECT-api/lib/structures/INotification";
import { IPageINotification } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageINotification";
import typia from "typia";

export async function test_list_notifications_with_filters_and_pagination(connection: api.IConnection): Promise<void> {
  // Step 1: Seed notifications with diverse values
  const userA = crypto.randomUUID();
  const userB = crypto.randomUUID();
  const threadIdA = crypto.randomUUID();
  const threadIdB = crypto.randomUUID();

  // Notifications:
  // - userA: system(SYSTEM, unread), message(MESSAGE, read), promo(PROMO, unread), diff threads/timestamps
  // - userB: only promo
  const now = new Date();
  const oneHourAgo = new Date(now.getTime() - 3600 * 1000).toISOString();
  const twoHourAgo = new Date(now.getTime() - 7200 * 1000).toISOString();
  const oneHourLater = new Date(now.getTime() + 3600 * 1000).toISOString();

  // Create notification for userA - type SYSTEM, unread
  const notifA1: INotification = await api.functional.communicationReview.notifications.post(connection, {
    body: {
      user_id: userA,
      notification_type: "SYSTEM",
      content: "System alert for userA (unread)",
      thread_id: threadIdA,
    },
  });
  // Create notification for userA - type MESSAGE, read
  const notifA2: INotification = await api.functional.communicationReview.notifications.post(connection, {
    body: {
      user_id: userA,
      notification_type: "MESSAGE",
      content: "Message for userA (read)",
      thread_id: threadIdA,
    },
  });
  // Mark notifA2 as read manually (simulate read by setting read_at field if allowed in test env)
  notifA2.read_at = now.toISOString();

  // Create notification for userA - type PROMO, unread, different thread
  const notifA3: INotification = await api.functional.communicationReview.notifications.post(connection, {
    body: {
      user_id: userA,
      notification_type: "PROMO",
      content: "Promo for userA (unread)",
      thread_id: threadIdB,
    },
  });
  // Create notification for userB - type PROMO, unread
  const notifB1: INotification = await api.functional.communicationReview.notifications.post(connection, {
    body: {
      user_id: userB,
      notification_type: "PROMO",
      content: "Promo for userB (unread)",
      thread_id: null,
    },
  });

  // Step 2: Test filter by user_id
  const userAResult: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { user_id: userA },
  });
  typia.assert<IPageINotification>(userAResult);
  TestValidator.predicate("user_id filter matches only userA")(() => userAResult.data.every(n => n.user_id === userA));

  // Step 3: Test filter by notification_type
  const systemTypeResult: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { notification_type: "SYSTEM" },
  });
  typia.assert<IPageINotification>(systemTypeResult);
  TestValidator.predicate("notification_type filter SYSTEM")(() => systemTypeResult.data.every(n => n.notification_type === "SYSTEM"));

  // Step 4: Test filter by thread_id
  const threadAResult: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { thread_id: threadIdA },
  });
  typia.assert<IPageINotification>(threadAResult);
  TestValidator.predicate("thread_id filter matches threadIdA")(() => threadAResult.data.every(n => n.thread_id === threadIdA));

  // Step 5: Test filter by archived
  const archivedResult: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { archived: true },
  });
  typia.assert<IPageINotification>(archivedResult);
  TestValidator.predicate("archived filter=true gets only archived")(() => archivedResult.data.every(n => n.archived === true));

  // Step 6: Test sent_at after/before
  const afterNowResult: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { sent_after: oneHourLater },
  });
  typia.assert<IPageINotification>(afterNowResult);
  TestValidator.equals("no notifications after a far future date")([] as INotification[])(afterNowResult.data);

  // Step 7: Pagination filter (page/limit)
  const pagedResult: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { user_id: userA, page: 1, limit: 2 },
  });
  typia.assert<IPageINotification>(pagedResult);
  TestValidator.predicate("paged limit works")(() => pagedResult.data.length <= 2);
  TestValidator.equals("paged records matches pagination.records")(
    pagedResult.data.length,
  )(pagedResult.pagination.records <= 2 ? pagedResult.pagination.records : 2);

  // Step 8: No-match filter
  const noMatchResult: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
    body: { user_id: userA, notification_type: "DOES_NOT_EXIST" },
  });
  typia.assert<IPageINotification>(noMatchResult);
  TestValidator.equals("no notifications with type DOES_NOT_EXIST")([] as INotification[])(noMatchResult.data);
}

