import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IReview } from "@ORGANIZATION/PROJECT-api/lib/structures/IReview";
import { IPageIReview } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIReview";
import typia from "typia";

/**
 * Test listing and searching reviews with various filters, paging, and edge cases.
 */
export async function test_list_reviews_with_various_filters(
  connection: api.IConnection
): Promise<void> {
  // 1. Create a test review
  const createInput: IReview.ICreate = {
    user_id: typia.random<string & typia.tags.Format<"uuid">>(),
    product_id: typia.random<string & typia.tags.Format<"uuid">>(),
    content: "Test review for filter testing.",
    language_code: "en",
    verified_purchase: true,
  };
  const created: IReview = await api.functional.communicationReview.reviews.post(connection, { body: createInput });
  typia.assert<IReview>(created);

  // 2. List with no filters (should include at least the created review)
  const result_all = await api.functional.communicationReview.reviews.patch(connection, { body: {} });
  typia.assert<IPageIReview>(result_all);
  TestValidator.predicate("Should list at least one review")(() => result_all.data.length >= 1);

  // 3. Filter by reviewer (user_id)
  const by_user = await api.functional.communicationReview.reviews.patch(connection, { body: { user_id: created.user_id } });
  typia.assert<IPageIReview>(by_user);
  TestValidator.predicate("All reviews should be by the test user")(() => by_user.data.every(r => r.user_id === created.user_id));

  // 4. Filter by product
  const by_product = await api.functional.communicationReview.reviews.patch(connection, { body: { product_id: created.product_id } });
  typia.assert<IPageIReview>(by_product);
  TestValidator.predicate("All reviews should be for the test product")(() => by_product.data.every(r => r.product_id === created.product_id));

  // 5. Filter by language_code
  const by_language = await api.functional.communicationReview.reviews.patch(connection, { body: { language_code: created.language_code } });
  typia.assert<IPageIReview>(by_language);
  TestValidator.predicate("All reviews should be in the requested language")(() => by_language.data.every(r => r.language_code === created.language_code));

  // 6. Filter by verified_purchase true
  const by_verified = await api.functional.communicationReview.reviews.patch(connection, { body: { verified_purchase: true } });
  typia.assert<IPageIReview>(by_verified);
  TestValidator.predicate("All reviews should have verified_purchase true")(() => by_verified.data.every(r => r.verified_purchase === true));

  // 7. Filter by creation date ranges
  // a) Range that should include the created review
  const createdAfterPast = await api.functional.communicationReview.reviews.patch(connection, { body: { created_after: "2000-01-01T00:00:00.000Z" } });
  typia.assert<IPageIReview>(createdAfterPast);
  TestValidator.predicate("Should include the created review")(() => createdAfterPast.data.some(r => r.id === created.id));

  // b) Range that excludes the created review
  const createdBeforePast = await api.functional.communicationReview.reviews.patch(connection, { body: { created_before: "2000-01-01T00:00:00.000Z" } });
  typia.assert<IPageIReview>(createdBeforePast);
  TestValidator.predicate("Should return empty data")(() => createdBeforePast.data.length === 0);

  // 8. Filter by archived=false (should include the new review as not archived)
  const by_archived_false = await api.functional.communicationReview.reviews.patch(connection, { body: { archived: false } });
  typia.assert<IPageIReview>(by_archived_false);
  TestValidator.predicate("None should be archived")(() => by_archived_false.data.every(r => r.archived === false));

  // 9. Paging test (limit=1, page=1)
  const paged = await api.functional.communicationReview.reviews.patch(connection, { body: { limit: 1, page: 1 } });
  typia.assert<IPageIReview>(paged);
  TestValidator.predicate("Pagination meta correct: limit should be 1")(() => paged.pagination.limit === 1);
  TestValidator.predicate("Should have at most one review returned")(() => paged.data.length <= 1);

  // 10. Extreme filter: random user_id & product_id (should return empty)
  const emptyResult = await api.functional.communicationReview.reviews.patch(connection, { body: { user_id: typia.random<string & typia.tags.Format<"uuid">>(), product_id: typia.random<string & typia.tags.Format<"uuid">>() } });
  typia.assert<IPageIReview>(emptyResult);
  TestValidator.equals("Should return empty data array")([] as IReview[])(emptyResult.data);
  TestValidator.predicate("Pagination total records is 0")(() => emptyResult.pagination.records === 0);
}

