/**
 * Test archiving (soft-deleting) a review, idempotency, and 404 on invalid ID.
 */
export async function test_soft_delete_review_and_idempotency_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a review
  const created: IReview = await api.functional.communicationReview.reviews.post(
    connection,
    {
      body: typia.random<IReview.ICreate>(),
    },
  );
  typia.assert(created);

