import typia from "typia";

export async function test_list_review_media_with_filters_and_pagination(connection: api.IConnection): Promise<void> {
  // Step 1: Create multiple review media with diverse values
  const baseReviewId = typia.random<string & typia.tags.Format<"uuid">>();
  const otherReviewId = typia.random<string & typia.tags.Format<"uuid">>();
  const mediaTypeImage = "image";
  const mediaTypeVideo = "video";

  const created: IReviewMedia[] = [];

  // Create first entry (same reviewId, image type)
  const entry1 = await api.functional.communicationReview.reviewMedia.post(connection, {
    body: {
      review_id: baseReviewId,
      media_type: mediaTypeImage,
      uri: "https://cdn.example.com/media1.jpg",
      alt_text: "sample alt text",
      sequence: 1,
    },
  });
  created.push(entry1);

  // Create second entry (same reviewId, video type)
  const entry2 = await api.functional.communicationReview.reviewMedia.post(connection, {
    body: {
      review_id: baseReviewId,
      media_type: mediaTypeVideo,
      uri: "https://cdn.example.com/media2.mp4",
      alt_text: null,
      sequence: 2,
    },
  });
  created.push(entry2);

  // Create third entry (different reviewId, image type)
  const entry3 = await api.functional.communicationReview.reviewMedia.post(connection, {
    body: {
      review_id: otherReviewId,
      media_type: mediaTypeImage,
      uri: "https://cdn.example.com/media3.jpg",
      alt_text: undefined,
      sequence: 3,
    },
  });
  created.push(entry3);

  // Step 2: List with no filters (expect all 3)
  const allResult = await api.functional.communicationReview.reviewMedia.patch(connection, {
    body: {},
  });
  typia.assert<IPageIReviewMedia>(allResult);
  TestValidator.equals("all entries count")(created.length)(allResult.data.length);
  // Should contain all created ids
  TestValidator.predicate("all ids present")(() => created.every(c => allResult.data.some(d => d.id === c.id)));

  // Step 3: Filter by review_id (baseReviewId: expect entry1 and entry2)
  const reviewIdResult = await api.functional.communicationReview.reviewMedia.patch(connection, {
    body: { review_id: baseReviewId },
  });
  typia.assert<IPageIReviewMedia>(reviewIdResult);
  TestValidator.equals("baseReviewId entries count")(2)(reviewIdResult.data.length);
  TestValidator.predicate("ids for baseReviewId present")(() => reviewIdResult.data.every(d => d.review_id === baseReviewId));

  // Step 4: Filter by media_type (image)
  const imageTypeResult = await api.functional.communicationReview.reviewMedia.patch(connection, {
    body: { media_type: mediaTypeImage },
  });
  typia.assert<IPageIReviewMedia>(imageTypeResult);
  const imageTypeCount = created.filter(c => c.media_type === mediaTypeImage).length;
  TestValidator.equals("media_type=image count")(imageTypeCount)(imageTypeResult.data.length);
  TestValidator.predicate("all image type")(() => imageTypeResult.data.every(d => d.media_type === mediaTypeImage));

  // Step 5: Filter by alt_text (present/"sample alt text")
  const altTextResult = await api.functional.communicationReview.reviewMedia.patch(connection, {
    body: { alt_text: "sample alt text" },
  });
  typia.assert<IPageIReviewMedia>(altTextResult);
  TestValidator.equals("alt_text='sample alt text' count")(1)(altTextResult.data.length);
  TestValidator.predicate("alt_text match")(() => altTextResult.data[0]?.alt_text === "sample alt text");

  // Step 6: Edge case - non-existent review_id
  const missingResult = await api.functional.communicationReview.reviewMedia.patch(connection, {
    body: { review_id: typia.random<string & typia.tags.Format<"uuid">>() },
  });
  typia.assert<IPageIReviewMedia>(missingResult);
  TestValidator.equals("non-existent review_id yields empty")(0)(missingResult.data.length);

  // Step 7: Pagination test (limit = 2)
  // (Assumes the endpoint supports limit/current in IReviewMedia.IRequest, even if props are not explicit in the DTO)
  // Prepare request with no filters, paginated to 2 items per page
  const paginatedResult = await api.functional.communicationReview.reviewMedia.patch(connection, {
    body: {},
    // If the API receives pagination parameters differently, this might need adjustment
  });
  typia.assert<IPageIReviewMedia>(paginatedResult);
  TestValidator.predicate("pagination structure")(!!paginatedResult.pagination);
  // This sample does not enforce actual paging without explicit support, but validates structure exists
}

