    ai_model_version_id: null,
  };
  const created = await api.functional.paymentsDiscounts.discountRules.post(connection, { body: createInput });
  typia.assert<IDiscountRule>(created);

  // 2. Update mutable fields (should succeed)
  const updateInput: IDiscountRule.IUpdate = {
    value: 15,
    campaign_id: created.campaign_id,
    active: false,
    ai_model_version_id: null,
  };
  const updated = await api.functional.paymentsDiscounts.discountRules.putById(connection, {
