  TestValidator.equals("created_at is unchanged")<string>(created.created_at)(updated.created_at);
  TestValidator.equals("value updated")<number>(updateInput.value!)(updated.value);
  TestValidator.equals("active updated")<boolean>(updateInput.active!)(updated.active);

  // 3. Try update on non-existent ID (should error)
