  await TestValidator.error("cannot update inactive/locked rule")(async () => {
    await api.functional.paymentsDiscounts.discountRules.putById(connection, {
      id: created.id,
      body: { value: 21 },
    });
  });

  // 5. Policy violation attempt (simulate invalid value, e.g. over 100% for percentage type)
