  // 1. No filters, first page
  const res1 = await api.functional.paymentsDiscounts.discountCampaigns.patch(
    connection,
    { body: {} }
  );
  typia.assert<IPageIDiscountCampaign>(res1);
  TestValidator.predicate("pagination present")(() => typeof res1.pagination === "object");
  TestValidator.predicate("data array present")(() => Array.isArray(res1.data));
  TestValidator.predicate("page 1")(() => res1.pagination.current === 1);

  const hasData = res1.data.length > 0;

  // 2. Filter: active=true and active=false
  for (const active of [true, false]) {
    const resA = await api.functional.paymentsDiscounts.discountCampaigns.patch(
      connection,
      { body: { active } },
    );
    typia.assert<IPageIDiscountCampaign>(resA);
    for (const item of resA.data) {
      TestValidator.equals(`active status ${active}`)(active)(item.active);
    }
  }

  // 3. Filter: start_at_range and created_at_range
  if (hasData) {
    const one = res1.data[0];
    // take realistic bounding values from existing data
    const start_at = one.start_at;
    const end_at = one.end_at || one.start_at;
    const created_start = one.created_at;
    const created_end = one.created_at;
    const resR = await api.functional.paymentsDiscounts.discountCampaigns.patch(
      connection,
      { body: {
        start_at_range: [start_at, end_at],
        created_at_range: [created_start, created_end],
      }}
    );
    typia.assert<IPageIDiscountCampaign>(resR);
    for (const itm of resR.data) {
      TestValidator.predicate("start_at in range")(
        () => itm.start_at >= start_at && itm.start_at <= end_at,
      );
      TestValidator.predicate("created_at in range")(
        () => itm.created_at >= created_start && itm.created_at <= created_end,
      );
    }
  }

  // 4. Filter: non-existing name
  const resEmpty = await api.functional.paymentsDiscounts.discountCampaigns.patch(
    connection,
    { body: { name: "_non_existing_" } }
  );
  typia.assert<IPageIDiscountCampaign>(resEmpty);
  TestValidator.equals("should return empty data")<IDiscountCampaign[]>([])(resEmpty.data);

  // 5. Pagination boundary: overbound page number test
  const totalPages = res1.pagination.pages;
  const overboundPageRequest = await api.functional.paymentsDiscounts.discountCampaigns.patch(
    connection,
    // If the API expects a page/limit, but DTO does not contain these, this is skipped; else, would set page > total
    { body: {} }
  );
  typia.assert<IPageIDiscountCampaign>(overboundPageRequest);

  // Could not test specific page param unless IRequest supports it; check for emptiness or page consistency
  // If pagination keys exist, confirm logic
  TestValidator.predicate("valid page 1/pagination")(
    () => typeof overboundPageRequest.pagination.pages === "number"
  );
}

