import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IDiscountCampaign } from "@ORGANIZATION/PROJECT-api/lib/structures/IDiscountCampaign";
import { IDiscountRule } from "@ORGANIZATION/PROJECT-api/lib/structures/IDiscountRule";
import typia from "typia";

export async function test_create_discount_campaign_with_validation_and_uniqueness(connection: api.IConnection): Promise<void> {
    // 1. Create a valid discount rule to use its id (for later association)
    const discountRuleInput: IDiscountRule.ICreate = {
        type: "percentage",
        value: 10,
        active: true
    };
    const discountRule = await api.functional.paymentsDiscounts.discountRules.post(connection, { body: discountRuleInput });
    typia.assert<IDiscountRule>(discountRule);

    // 2. Create a discount campaign with valid required attributes
    const fixedCampaignName = `E2E-Test-Campaign-${Date.now()}`;
    const validStartAt = new Date(Date.now() + 1000 * 60).toISOString(); // 1min in future
    const validEndAt = new Date(Date.now() + 1000 * 60 * 60 * 24).toISOString(); // 1 day in future
    const campaignInput: IDiscountCampaign.ICreate = {
        name: fixedCampaignName,
        description: "E2E Discount Campaign test.",
        start_at: validStartAt,
        end_at: validEndAt,
        active: true
    };
    const createdCampaign = await api.functional.paymentsDiscounts.discountCampaigns.post(connection, { body: campaignInput });
    typia.assert<IDiscountCampaign>(createdCampaign);
    TestValidator.equals("campaign name matches")(campaignInput.name)(createdCampaign.name);
    TestValidator.equals("campaign active matches")(campaignInput.active)(createdCampaign.active);
    TestValidator.equals("start_at matches")(campaignInput.start_at)(createdCampaign.start_at);
    TestValidator.equals("end_at matches")(campaignInput.end_at)(createdCampaign.end_at);
    if (campaignInput.description)
        TestValidator.equals("description matches")(campaignInput.description)(createdCampaign.description ?? undefined);

    // 3. Attempt to create with missing required fields (no name)
    const missingNameInput: IDiscountCampaign.ICreate = {
        // @ts-expect-error
        start_at: validStartAt,
        active: true
    };
    await TestValidator.error("campaign creation should fail without name")(() =>
        api.functional.paymentsDiscounts.discountCampaigns.post(connection, { body: missingNameInput })
    );

    // 4. Attempt to create with missing required fields (no start_at)
    const missingStartAtInput: IDiscountCampaign.ICreate = {
        name: `E2E-No-StartAt-${Date.now()}`,
        active: true
    };
    await TestValidator.error("campaign creation should fail without start_at")(() =>
        api.functional.paymentsDiscounts.discountCampaigns.post(connection, { body: missingStartAtInput })
    );

    // 5. Attempt to create campaign with duplicate name (should fail due to uniqueness constraint)
    const duplicateCampaignInput: IDiscountCampaign.ICreate = {
        name: fixedCampaignName,
        description: "Should fail - duplicate name.",
        start_at: validStartAt,
        end_at: validEndAt,
        active: true
    };
    await TestValidator.error("duplicate campaign name should be rejected")(() =>
        api.functional.paymentsDiscounts.discountCampaigns.post(connection, { body: duplicateCampaignInput })
    );

    // 6. (Logical) Association: Attempt to create a discount rule and associate it with the created campaign
    const ruleWithCampaignInput: IDiscountRule.ICreate = {
        campaign_id: createdCampaign.id,
        type: "fixed",
        value: 5000,
        active: true
    };
    const associatedRule = await api.functional.paymentsDiscounts.discountRules.post(connection, { body: ruleWithCampaignInput });
    typia.assert<IDiscountRule>(associatedRule);
    TestValidator.equals("associated rule campaign_id matches created campaign")(
        createdCampaign.id
    )(associatedRule.campaign_id);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IDiscountCampaign } from "@ORGANIZATION/PROJECT-api/lib/structures/IDiscountCampaign";
import typia, { tags } from "typia";

function addDays(dateStr: string, days: number): string {
  const date = new Date(dateStr);
  date.setDate(date.getDate() + days);
  return date.toISOString();
}

export async function test_update_discount_campaign_and_check_business_rules(connection: api.IConnection): Promise<void> {
  // 1. Create a baseline campaign
  const initialStart = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString(); // tomorrow
  const initialEnd = addDays(initialStart, 3);                                   // +3 days
  const campaignCreate: IDiscountCampaign.ICreate = {
    name: `E2E_Campaign_${Date.now()}`,
    description: "E2E discount campaign for update test",
    start_at: initialStart,
    end_at: initialEnd,
    active: true,
  };
  const created = await api.functional.paymentsDiscounts.discountCampaigns.post(connection, {
    body: campaignCreate,
  });
  typia.assert<IDiscountCampaign>(created);

  // 2. Update campaign details (Positive scenario)
  const newName = `${campaignCreate.name}_Updated`;
  const newDesc = "Updated for E2E test.";
  const updated = await api.functional.paymentsDiscounts.discountCampaigns.putById(connection, {
    id: created.id,
    body: {
      name: newName,
      description: newDesc,
      start_at: addDays(created.start_at, 1), // shift by +1d
      end_at: addDays(created.end_at ?? created.start_at, 2), // extend end by 2 days
      active: true,
    },
  });
  typia.assert<IDiscountCampaign>(updated);
  TestValidator.equals("name change")<string>(newName)(updated.name);
  TestValidator.equals("description change")<string>(newDesc)(updated.description ?? "");
  TestValidator.predicate("start_at changed")(() => updated.start_at > created.start_at);
  TestValidator.predicate("end_at extended")(() => (updated.end_at ?? "") > (created.end_at ?? ""));
  TestValidator.equals("active stays true")<boolean>(true)(updated.active);

  // 3. Attempt to change immutable fields (should be rejected/ignored)
  await TestValidator.error("Cannot update immutable fields: id, created_at")(() =>
    api.functional.paymentsDiscounts.discountCampaigns.putById(connection, {
      id: created.id,
      body: {
        // @ts-expect-error - purposely trying immutable fields
        id: "some-other-id",
        // @ts-expect-error
        created_at: new Date().toISOString(),
      } as any,
    })
  );

  // 4. Update to inactive while changing dates
  const updateInactive = await api.functional.paymentsDiscounts.discountCampaigns.putById(connection, {
    id: created.id,
    body: {
      active: false,
      description: "Setting inactive for E2E.",
      end_at: addDays(updated.start_at, 1),
    },
  });
  typia.assert<IDiscountCampaign>(updateInactive);
  TestValidator.equals("set inactive")<boolean>(false)(updateInactive.active);

  // 5. Attempt to update past campaign
  const oldStart = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString(); // 7 days ago
  const oldEnd = addDays(oldStart, 1);                                            // 6d ago
  const pastCampaign = await api.functional.paymentsDiscounts.discountCampaigns.post(connection, {
    body: {
      ...campaignCreate,
      name: `${campaignCreate.name}_Past`,
      start_at: oldStart,
      end_at: oldEnd,
    },
  });
  typia.assert<IDiscountCampaign>(pastCampaign);
  await TestValidator.error("Should not update ended campaign")(() =>
    api.functional.paymentsDiscounts.discountCampaigns.putById(connection, {
      id: pastCampaign.id,
      body: {
        description: "Illegal update to finished campaign.",
      },
    })
  );

  // 6. Attempt overlapping campaign window on update
  const overlapCampaign = await api.functional.paymentsDiscounts.discountCampaigns.post(connection, {
    body: {
      ...campaignCreate,
      name: `${campaignCreate.name}_overlap_target`,
      start_at: addDays(initialStart, 10), // separate window
      end_at: addDays(initialEnd, 14),
    },
  });
  typia.assert<IDiscountCampaign>(overlapCampaign);
  // Try to update overlapCampaign to overlap with "created" campaign
  await TestValidator.error("Should not allow date window overlap")(() =>
    api.functional.paymentsDiscounts.discountCampaigns.putById(connection, {
      id: overlapCampaign.id,
      body: {
        start_at: created.start_at, // now overlaps with original
        end_at: created.end_at,
      },
    })
  );
}

