// Utility to generate a random UUID (very unlikely to exist in fixture DB)
function generateRandomUuid(): string {
  // example: 8-4-4-4-12
  return "00000000-0000-4000-8000-000000000000".replace(/[048]/g, c => (Math.random() * 16 | 0).toString(16));
}

export async function test_list_transactions_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // --- 1. Basic valid filter: retrieve a page with no filters (should return data if DB is not empty) ---
  const resp1 = await api.functional.paymentsDiscounts.transactions.patch(connection, {
    body: {},
  });
  typia.assert<IPageITransaction>(resp1);
  TestValidator.predicate("basic listing has pagination")(() => !!resp1.pagination);
  TestValidator.predicate("returned transactions are in correct format")(() => Array.isArray(resp1.data));
  resp1.data.forEach(tx => typia.assert<ITransaction>(tx));

  // --- 2. Filter by a known nonsense UUID for user_id: should return empty data[] ---
  const resp2 = await api.functional.paymentsDiscounts.transactions.patch(connection, {
    body: { user_id: generateRandomUuid() },
  });
  typia.assert<IPageITransaction>(resp2);
  TestValidator.equals("empty result for unknown user")( <ITransaction[]>[])(resp2.data);

  // --- 3. Invalid page number (zero, should still return valid IPageITransaction with no crash) ---
  const resp3 = await api.functional.paymentsDiscounts.transactions.patch(connection, {
    body: { page: 0 },
  });
  typia.assert<IPageITransaction>(resp3);
  // If the API does not throw, must always provide pagination
  TestValidator.predicate("page=0 returns pagination structure")(() => !!resp3.pagination && typeof resp3.pagination.current === "number");

  // --- 4. Large limit (e.g., 1000): API should not crash, pagination reflects request ---
  const resp4 = await api.functional.paymentsDiscounts.transactions.patch(connection, {
    body: { limit: 1000 },
  });
  typia.assert<IPageITransaction>(resp4);
  TestValidator.predicate("large limit does not break API")(() => !!resp4.pagination && resp4.pagination.limit === 1000);

  // --- 5. Filter for order_id is null (should find some or none; focus on shape) ---
  const resp5 = await api.functional.paymentsDiscounts.transactions.patch(connection, {
    body: { order_id: null },
  });
  typia.assert<IPageITransaction>(resp5);
  resp5.data.forEach(tx => typia.assert<ITransaction>(tx));

  // --- 6. Filter with created_after/created_before as now (should be restrictive, likely empty) ---
  const nowIso = new Date().toISOString();
  const resp6 = await api.functional.paymentsDiscounts.transactions.patch(connection, {
    body: { created_after: nowIso, created_before: nowIso },
  });
  typia.assert<IPageITransaction>(resp6);
  // Likely to be empty due to timestamp exclusivity
  TestValidator.predicate("date filter returns array")(() => Array.isArray(resp6.data));

  // --- 7. Invalid (huge) page number (e.g., 1e9): should not crash, likely empty
  const resp7 = await api.functional.paymentsDiscounts.transactions.patch(connection, {
    body: { page: 1e9 },
  });
  typia.assert<IPageITransaction>(resp7);
  TestValidator.predicate("page overflow handled gracefully")(() => Array.isArray(resp7.data));

  // --- 8. Combination filters (type + user_id, if any results) ---
  if (resp1.data.length > 0) {
    const sample = resp1.data[0];
    const resp8 = await api.functional.paymentsDiscounts.transactions.patch(connection, {
      body: { user_id: sample.user_id, type: sample.type },
    });
    typia.assert<IPageITransaction>(resp8);
    resp8.data.forEach(tx => {
      typia.assert<ITransaction>(tx);
      TestValidator.equals("user_id matches")<string>(sample.user_id)(tx.user_id);
      TestValidator.equals("type matches")<string>(sample.type)(tx.type);
    });
  }
}

