import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { ITransaction } from "@ORGANIZATION/PROJECT-api/lib/structures/ITransaction";

export async function test_create_transaction_with_success_and_error_validation(
  connection: api.IConnection,
): Promise<void> {
  // 1. Successful create with all required fields
  const valid: ITransaction.ICreate = {
    user_id: typia.random<string & typia.tags.Format<"uuid">>(),
    type: "purchase",
    created_at: new Date().toISOString(),
    order_id: typia.random<string & typia.tags.Format<"uuid">>(),
    description: "Test purchase transaction",
  };
  const res = await api.functional.paymentsDiscounts.transactions.post(connection, { body: valid });
  typia.assert<ITransaction>(res);
  TestValidator.predicate("transaction id must be uuid")(() => /^([0-9a-fA-F]{8}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{12})$/.test(res.id));
  TestValidator.equals("transaction user_id")(valid.user_id)(res.user_id);
  TestValidator.equals("transaction type")(valid.type)(res.type);
  TestValidator.equals("created_at matches")(valid.created_at)(res.created_at);
  TestValidator.equals("has description")(valid.description)(res.description);
  // order_id is optional, allow null/undefined equality
  TestValidator.equals("order_id matches")(valid.order_id ?? null)(res.order_id ?? null);

  // 2. Missing required fields
  const required: (keyof ITransaction.ICreate)[] = ["user_id", "type", "created_at"];
  for (const field of required) {
    const copy = { ...valid };
    delete copy[field];
    await TestValidator.httpError(`missing required ${field}`)(400, 422)(
      () => api.functional.paymentsDiscounts.transactions.post(connection, { body: copy as any })
    );
  }

  // 3. Invalid UUIDs
  const invalidUuidObj: ITransaction.ICreate = {
    ...valid,
    user_id: "not-a-uuid" as any,
    order_id: "xxx-bad-uuid" as any,
  };
  await TestValidator.httpError("invalid user_id/order_id uuid format")(400, 422)(
    () => api.functional.paymentsDiscounts.transactions.post(connection, { body: invalidUuidObj })
  );

  // 4. Disallowed or empty type
  const emptyTypeObj: ITransaction.ICreate = {
    ...valid,
    type: "",
  };
  await TestValidator.httpError("empty type")(400, 422)(
    () => api.functional.paymentsDiscounts.transactions.post(connection, { body: emptyTypeObj })
  );

  // 5. Null type (invalid, as type should be string)
  const nullTypeObj = { ...valid, type: null } as any;
  await TestValidator.httpError("null type")(400, 422)(
    () => api.functional.paymentsDiscounts.transactions.post(connection, { body: nullTypeObj })
  );

  // 6. Malformed payload (not an object)
  await TestValidator.httpError("malformed payload")(400, 422)(
    () => api.functional.paymentsDiscounts.transactions.post(connection, { body: "not an object" as any })
  );
}

