import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPageIVoucher } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIVoucher";
import { IVoucher } from "@ORGANIZATION/PROJECT-api/lib/structures/IVoucher";
import typia from "typia";

export async function test_list_vouchers_with_multiple_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // ---------- SETUP: Get sample data for testing ----------
  // Get all vouchers (no filter, first page, limit 20)
  const allVouchers: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
    body: { page: 1, limit: 20 },
  });
  typia.assert<IPageIVoucher>(allVouchers);

  // If none, skip tests needing real data
  if (allVouchers.data.length === 0) return;
  const firstVoucher = allVouchers.data[0];
  const testUserId = firstVoucher.user_id;
  const testVoucherHasExpiry = allVouchers.data.find(v => !!v.expires_at);
  const now = new Date().toISOString();

  // ---------- 1. Filter by user_id ----------
  const byUser: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
    body: { user_id: testUserId, page: 1, limit: 20 },
  });
  typia.assert<IPageIVoucher>(byUser);
  TestValidator.predicate("all vouchers belong to user")(() =>
    byUser.data.every(v => v.user_id === testUserId),
  );

  // ---------- 2. Filter by active status (expires_after now) ----------
  if (testVoucherHasExpiry) {
    const active: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
      body: { expires_after: now, page: 1, limit: 20 },
    });
    typia.assert<IPageIVoucher>(active);
    TestValidator.predicate("no expired vouchers returned (active status)")(() =>
      active.data.every(v => !v.expires_at || v.expires_at > now),
    );
  }

  // ---------- 3. Filter by expired status (expires_before now) ----------
  if (testVoucherHasExpiry) {
    const expired: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
      body: { expires_before: now, page: 1, limit: 20 },
    });
    typia.assert<IPageIVoucher>(expired);
    TestValidator.predicate("all vouchers expired")(() =>
      expired.data.every(v => v.expires_at !== undefined && v.expires_at !== null && v.expires_at < now),
    );
  }

  // ---------- 4. Filter by used (redeemed) status (redeemed = true) ----------
  const used: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
    body: { redeemed: true, page: 1, limit: 20 },
  });
  typia.assert<IPageIVoucher>(used);
  TestValidator.predicate("all redeemed vouchers have redeemed_at")(() =>
    used.data.every(v => v.redeemed_at != null),
  );

  // ---------- 5. Filter by unused (redeemed = false) ----------
  const unused: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
    body: { redeemed: false, page: 1, limit: 20 },
  });
  typia.assert<IPageIVoucher>(unused);
  TestValidator.predicate("all unredeemed vouchers missing redeemed_at")(() =>
    unused.data.every(v => !v.redeemed_at),
  );

  // ---------- 6. Filter by expiration date range ----------
  if (testVoucherHasExpiry) {
    const from = testVoucherHasExpiry.expires_at!;
    const to = new Date(new Date(from).getTime() + 24 * 60 * 60 * 1000).toISOString();
    const inRange: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
      body: { expires_after: from, expires_before: to, page: 1, limit: 20 },
    });
    typia.assert<IPageIVoucher>(inRange);
    TestValidator.predicate("all vouchers expire within range")(() =>
      inRange.data.every(v => v.expires_at && v.expires_at > from && v.expires_at < to),
    );
  }

  // ---------- 7. Pagination: get two pages and compare ----------
  const pageSize = 2;
  const firstPage: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
    body: { page: 1, limit: pageSize },
  });
  typia.assert<IPageIVoucher>(firstPage);
  const secondPage: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
    body: { page: 2, limit: pageSize },
  });
  typia.assert<IPageIVoucher>(secondPage);
  TestValidator.predicate("pagination page separation")(() =>
    JSON.stringify(firstPage.data) !== JSON.stringify(secondPage.data),
  );
  TestValidator.predicate("correct page size")(() =>
    firstPage.data.length <= pageSize && secondPage.data.length <= pageSize,
  );

  // ---------- 8. Edge: likely empty result (random user_id) ----------
  const randomUserId = "00000000-0000-0000-0000-000000000000";
  const empty: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
    body: { user_id: randomUserId, page: 1, limit: 5 },
  });
  typia.assert<IPageIVoucher>(empty);
  TestValidator.equals("empty result")([] as IVoucher[])(empty.data);

  // ---------- 9. Edge: page number beyond last ----------
  const pagedEmpty: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
    body: { page: 9999, limit: 10 },
  });
  typia.assert<IPageIVoucher>(pagedEmpty);
  TestValidator.predicate("empty for out-of-range page")(() =>
    pagedEmpty.data.length === 0,
  );

  // ---------- 10. Edge: very large/small limit ----------
  const smallLimit: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
    body: { page: 1, limit: 1 },
  });
  typia.assert<IPageIVoucher>(smallLimit);
  TestValidator.predicate("limit 1 returns at most 1 voucher")(() =>
    smallLimit.data.length <= 1,
  );
  const bigLimit: IPageIVoucher = await api.functional.paymentsDiscounts.vouchers.patch(connection, {
    body: { page: 1, limit: 100 },
  });
  typia.assert<IPageIVoucher>(bigLimit);
  TestValidator.predicate("limit 100 returns up to 100 vouchers")(() =>
    bigLimit.data.length <= 100,
  );
}

