export async function test_create_voucher_with_success_and_business_rule_enforcement(connection: api.IConnection): Promise<void> {
  // Step 1: Generate valid input
  const valid: IVoucher.ICreate = {
    user_id: "7de60e67-2fd8-4ccf-a0a9-046a1b13b44a",
    code: `TESTCODE_${Math.random().toString(36).substring(2, 10).toUpperCase()}`,
    balance: 1000.0,
    currency: "USD",
    expires_at: new Date(Date.now() + 1000 * 60 * 60 * 24 * 30).toISOString(),
  };

  // Step 2: Successful creation
  const created = await api.functional.paymentsDiscounts.vouchers.post(connection, { body: valid });
  typia.assert<IVoucher>(created);
  TestValidator.equals("voucher user_id matches")(valid.user_id)(created.user_id);
  TestValidator.equals("voucher code matches")(valid.code)(created.code);
  TestValidator.equals("voucher balance matches")(valid.balance)(created.balance);
  TestValidator.equals("voucher currency matches")(valid.currency)(created.currency);

  // Step 3a: Missing required field (user_id)
  await TestValidator.httpError("missing user_id")(400)(async () => {
    const input = { ...valid };
    // @ts-expect-error:
    delete input.user_id;
    await api.functional.paymentsDiscounts.vouchers.post(connection, { body: input });
  });

  // Step 3b: Missing code
  await TestValidator.httpError("missing code")(400)(async () => {
    const input = { ...valid };
    // @ts-expect-error:
    delete input.code;
    await api.functional.paymentsDiscounts.vouchers.post(connection, { body: input });
  });

  // Step 3c: Missing balance
  await TestValidator.httpError("missing balance")(400)(async () => {
    const input = { ...valid };
    // @ts-expect-error:
    delete input.balance;
    await api.functional.paymentsDiscounts.vouchers.post(connection, { body: input });
  });

  // Step 3d: Missing currency
  await TestValidator.httpError("missing currency")(400)(async () => {
    const input = { ...valid };
    // @ts-expect-error:
    delete input.currency;
    await api.functional.paymentsDiscounts.vouchers.post(connection, { body: input });
  });

  // Step 4a: Invalid negative balance
  await TestValidator.httpError("negative balance")(400, 422)(async () => {
    const input = { ...valid, balance: -100 };
    await api.functional.paymentsDiscounts.vouchers.post(connection, { body: input });
  });

  // Step 4b: Zero balance
  await TestValidator.httpError("zero balance")(400, 422)(async () => {
    const input = { ...valid, balance: 0 };
    await api.functional.paymentsDiscounts.vouchers.post(connection, { body: input });
  });

  // Step 4c: Empty code
  await TestValidator.httpError("empty code")(400, 422)(async () => {
    const input = { ...valid, code: "" };
    await api.functional.paymentsDiscounts.vouchers.post(connection, { body: input });
  });

  // Step 5: Duplicate code (should fail)
  // Use the code already created in 'valid'.
  await TestValidator.httpError("duplicate voucher code")(400, 409, 422)(async () => {
    const input = { ...valid, user_id: "e99dc5e8-2ecc-4623-8cf8-d370d3688c44" };
    await api.functional.paymentsDiscounts.vouchers.post(connection, { body: input });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IVoucher } from "@ORGANIZATION/PROJECT-api/lib/structures/IVoucher";
import typia, { tags } from "typia";

export async function test_update_voucher_metadata_and_handle_state_transitions(connection: api.IConnection): Promise<void> {
  // 1. Issue a test voucher
  const voucherInput: IVoucher.ICreate = {
    user_id: typia.random<string & tags.Format<"uuid">>(),
    code: `TEST-E2E-${Date.now()}`,
    balance: 1000,
    currency: "USD",
    expires_at: new Date(Date.now() + 10 * 24 * 3600 * 1000).toISOString(),
  };
  const created: IVoucher = await api.functional.paymentsDiscounts.vouchers.post(connection, { body: voucherInput });
  typia.assert<IVoucher>(created);
  TestValidator.equals("voucher must match issued user and code")<IVoucher>({ ...created })({ ...created });

  // 2. Update allowed metadata (set new expiry date & currency)
  const newExpiry = new Date(Date.now() + 30 * 24 * 3600 * 1000).toISOString();
  const updateAllowed: IVoucher.IUpdate = {
    expires_at: newExpiry,
    currency: "EUR",
  };
  const updated: IVoucher = await api.functional.paymentsDiscounts.vouchers.putById(connection, {
    id: created.id,
    body: updateAllowed,
  });
  typia.assert<IVoucher>(updated);
  TestValidator.equals("expiry/currency updated")(updateAllowed.expires_at)(updated.expires_at);
  TestValidator.equals("currency updated")(updateAllowed.currency)(updated.currency);

  // 3. Attempt to update an immutable field (simulate forbidden update)
  // Attempting to change code (should not be allowed by IVoucher.IUpdate, so we expect a type error if tried)
  // Instead, attempt to update balance after full redemption (simulate redeemed_at set)
  const redeemedVoucher: IVoucher = { ...updated, redeemed_at: new Date().toISOString() };
  // Attempt update on redeemed voucher
  await TestValidator.httpError("Updating redeemed voucher should be rejected")(400, 403)(async () =>
    await api.functional.paymentsDiscounts.vouchers.putById(connection, {
      id: redeemedVoucher.id,
      body: { balance: 999 },
    })
  );

  // 4. Attempt to update voucher with invalid/non-existent ID
  await TestValidator.httpError("Update with invalid voucher ID should be rejected")(404)(async () =>
    await api.functional.paymentsDiscounts.vouchers.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(),
      body: { expires_at: new Date(Date.now() + 86400000).toISOString() },
    })
  );
}

