import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { ILoyaltyPoint } from "@ORGANIZATION/PROJECT-api/lib/structures/ILoyaltyPoint";
import { IPageILoyaltyPoint } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageILoyaltyPoint";

/**
 * Test paginated listing and search for loyalty points ledgers.
 * Covers search by user, type, date range, and validates paginated navigation,
 * edge (empty) results, and structure typing.
 */
export async function test_list_loyalty_points_ledger_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // --- 1. Baseline: Query for known (or random) user --
  const baseInput: ILoyaltyPoint.IRequest = {
    // Provide only 'limit' for first page (simulate real-world queries)
    limit: 10,
    page: 1,
  };
  const baseResult: IPageILoyaltyPoint = await api.functional.paymentsDiscounts.loyaltyPoints.patch(connection, { body: baseInput });
  typia.assert<IPageILoyaltyPoint>(baseResult);

  TestValidator.predicate("array of loyalty point objects")(() =>
    Array.isArray(baseResult.data)
  );
  baseResult.data.forEach((entry) => {
    typia.assert<ILoyaltyPoint>(entry);
    TestValidator.predicate("required field: id")(() => typeof entry.id === "string");
    TestValidator.predicate("required field: user_id")(() => typeof entry.user_id === "string");
    TestValidator.predicate("required field: points")(() => typeof entry.points === "number");
    TestValidator.predicate("required field: type")(() => typeof entry.type === "string");
    TestValidator.predicate("required field: created_at")(() => typeof entry.created_at === "string");
  });

  // --- 2. Pagination: multi-page query ---
  if (baseResult.pagination.pages > 1) {
    const nextPageInput: ILoyaltyPoint.IRequest = {
      ...baseInput,
      page: 2,
    };
    const page2Result: IPageILoyaltyPoint = await api.functional.paymentsDiscounts.loyaltyPoints.patch(connection, { body: nextPageInput });
    typia.assert<IPageILoyaltyPoint>(page2Result);
    // Ensure no overlap between page 1 and 2 ids
    const page1Ids = baseResult.data.map((l) => l.id);
    const page2Ids = page2Result.data.map((l) => l.id);
    TestValidator.predicate("no ID overlap between pages")(
      () => page2Ids.every((id) => !page1Ids.includes(id))
    );
  }

  // --- 3. Filtering: by type ---
  const typeFilter = baseResult.data.length > 0 ? baseResult.data[0].type : "earn";
  const typeInput: ILoyaltyPoint.IRequest = {
    ...baseInput,
    type: typeFilter,
  };
  const typeResult: IPageILoyaltyPoint = await api.functional.paymentsDiscounts.loyaltyPoints.patch(connection, { body: typeInput });
  typia.assert<IPageILoyaltyPoint>(typeResult);
  // If data, every type should match
  if (typeResult.data.length > 0) {
    TestValidator.predicate("all entries match type filter")(
      () => typeResult.data.every((l) => l.type === typeFilter)
    );
  }

  // --- 4. Filtering: date range
  // Use min & max created_at in existing data, or set a future range (expect empty)
  if (baseResult.data.length > 0) {
    const minCreated = baseResult.data.reduce((min, l) => l.created_at < min ? l.created_at : min, baseResult.data[0].created_at);
    const maxCreated = baseResult.data.reduce((max, l) => l.created_at > max ? l.created_at : max, baseResult.data[0].created_at);
    const dateInput: ILoyaltyPoint.IRequest = {
      ...baseInput,
      created_from: minCreated,
      created_to: maxCreated,
    };
    const dateResult: IPageILoyaltyPoint = await api.functional.paymentsDiscounts.loyaltyPoints.patch(connection, { body: dateInput });
    typia.assert<IPageILoyaltyPoint>(dateResult);
    TestValidator.predicate("date-range filter")(
      () => dateResult.data.every((l) => l.created_at >= minCreated && l.created_at <= maxCreated)
    );
  }

  // --- 5. Edge: Empty: fake user_id (expect zero-row result) ---
  const emptyInput: ILoyaltyPoint.IRequest = {
    ...baseInput,
    user_id: "00000000-0000-0000-0000-000000000000", // surely not in DB
  };
  const emptyResult: IPageILoyaltyPoint = await api.functional.paymentsDiscounts.loyaltyPoints.patch(connection, { body: emptyInput });
  typia.assert<IPageILoyaltyPoint>(emptyResult);
  TestValidator.equals("data is empty array")<ILoyaltyPoint[]>([])(emptyResult.data);
}

