export async function test_create_loyalty_points_event_with_validation_and_business_rules(connection: api.IConnection): Promise<void> {
  // Helper: Generates a valid loyalty point payload
  function validPayload(overrides?: Partial<ILoyaltyPoint.ICreate>): ILoyaltyPoint.ICreate {
    return {
      user_id: "11111111-1111-1111-1111-111111111111",
      points: 100,
      type: "earn",
      transaction_id: "22222222-2222-2222-2222-222222222222",
      campaign_id: "33333333-3333-3333-3333-333333333333",
      ...overrides,
    };
  }

  // 1. Positive case
  const input = validPayload();
  const created = await api.functional.paymentsDiscounts.loyaltyPoints.post(connection, { body: input });
  typia.assert<ILoyaltyPoint>(created);
  TestValidator.equals("Created event user_id")<string>(input.user_id)(created.user_id);
  TestValidator.equals("Created event points")<number>(input.points)(created.points);
  TestValidator.equals("Created event type")<string>(input.type)(created.type);

  // 2. Invalid values: negative points (if not allowed)
  await TestValidator.error("negative points should fail")(() =>
    api.functional.paymentsDiscounts.loyaltyPoints.post(connection, { body: validPayload({ points: -100 }) })
  );

  // 2b. Excessively large value
  await TestValidator.error("excessive points should fail")(() =>
    api.functional.paymentsDiscounts.loyaltyPoints.post(connection, { body: validPayload({ points: 1e9 }) })
  );

  // 2c. Zero points (if not allowed)
  await TestValidator.error("zero points should fail")(() =>
    api.functional.paymentsDiscounts.loyaltyPoints.post(connection, { body: validPayload({ points: 0 }) })
  );

  // 3. Missing required: user_id
  await TestValidator.error("missing user_id should fail")(() =>
    api.functional.paymentsDiscounts.loyaltyPoints.post(connection, { body: { ...validPayload(), user_id: undefined as any } })
  );

  // 3b. Missing required: points
  await TestValidator.error("missing points should fail")(() => {
    const payload = { ...validPayload() } as any;
    delete payload.points;
    return api.functional.paymentsDiscounts.loyaltyPoints.post(connection, { body: payload });
  });

  // 3c. Missing required: type
  await TestValidator.error("missing type should fail")(() => {
    const payload = { ...validPayload() } as any;
    delete payload.type;
    return api.functional.paymentsDiscounts.loyaltyPoints.post(connection, { body: payload });
  });

  // 4. Business rule: Uniqueness on (user_id, transaction_id, type)
  const uniqueKeyPayload = validPayload({ transaction_id: "tx-unique-4444-4444-4444-444444444444" });
  const first = await api.functional.paymentsDiscounts.loyaltyPoints.post(connection, { body: uniqueKeyPayload });
  typia.assert<ILoyaltyPoint>(first);
  // Attempt duplicate
  await TestValidator.error("duplicate event should be rejected or enforced unique")(() =>
    api.functional.paymentsDiscounts.loyaltyPoints.post(connection, { body: uniqueKeyPayload })
  );

  // 5. If allowed, award 2 different types for same order/user
  const diffType1 = validPayload({ transaction_id: "tx-diff-5555-5555-5555-555555555555", type: "earn" });
  const diffType2 = validPayload({ transaction_id: "tx-diff-5555-5555-5555-555555555555", type: "bonus" });
  const resp1 = await api.functional.paymentsDiscounts.loyaltyPoints.post(connection, { body: diffType1 });
  const resp2 = await api.functional.paymentsDiscounts.loyaltyPoints.post(connection, { body: diffType2 });
  typia.assert<ILoyaltyPoint>(resp1);
  typia.assert<ILoyaltyPoint>(resp2);
  TestValidator.predicate("should be different event types")(() => resp1.type !== resp2.type);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ILoyaltyPoint } from "@ORGANIZATION/PROJECT-api/lib/structures/ILoyaltyPoint";
import typia, { tags } from "typia";

export async function test_update_loyalty_points_metadata_and_handle_immutable_fields(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a loyalty points ledger record
  const created: ILoyaltyPoint = await api.functional.paymentsDiscounts.loyaltyPoints.post(connection, {
    body: {
      user_id: typia.random<string & tags.Format<"uuid">>(),
      transaction_id: typia.random<string & tags.Format<"uuid">>(),
      campaign_id: typia.random<string & tags.Format<"uuid">>(),
      points: 100,
      type: "earn",
    },
  });
  typia.assert<ILoyaltyPoint>(created);

  // Save initial immutable fields
  const originalPoints = created.points;
  const originalCreatedAt = created.created_at;

  // 2. Attempt legal mutable metadata update (transaction_id, campaign_id, type)
  const updatedFields = {
    transaction_id: typia.random<string & tags.Format<"uuid">>(),
    campaign_id: typia.random<string & tags.Format<"uuid">>(),
    type: "bonus",
  };
  const updated: ILoyaltyPoint = await api.functional.paymentsDiscounts.loyaltyPoints.putById(connection, {
    id: created.id,
    body: updatedFields,
  });
  typia.assert<ILoyaltyPoint>(updated);

  // Verifying mutable fields are updated and immutable ones are unchanged
  TestValidator.equals("transaction_id should update")<string | null>(updatedFields.transaction_id)(updated.transaction_id ?? null);
  TestValidator.equals("campaign_id should update")<string | null>(updatedFields.campaign_id)(updated.campaign_id ?? null);
  TestValidator.equals("type should update")<string | null>(updatedFields.type)(updated.type);
  TestValidator.equals("points should not change")<number>(originalPoints)(updated.points);
  TestValidator.equals("created_at should not change")<string>(originalCreatedAt)(updated.created_at);

  // 3. Attempt illegal update (immutable: points)
  await TestValidator.httpError("Should fail to update immutable field: points")(422, 400, 409)(async () => {
    await api.functional.paymentsDiscounts.loyaltyPoints.putById(connection, {
      id: created.id,
      body: { points: 1000 }, // illegal attempt to change 'points' post-creation
    });
  });

  // 4. Attempt update with invalid/nonexistent record ID
  const invalidId = typia.random<string & tags.Format<"uuid">>() + "-invalid";
  await TestValidator.httpError("Update with bad ID should not succeed")(404, 400)(async () => {
    await api.functional.paymentsDiscounts.loyaltyPoints.putById(connection, {
      id: invalidId as any,
      body: { type: "redeem" },
    });
  });

  // Note: No direct lock/revoked simulation via API surface,
  // would require additional endpoints or business state trigger hooks.
}

