import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IRefund } from "@ORGANIZATION/PROJECT-api/lib/structures/IRefund";
import { IPageIRefund } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIRefund";
import typia from "typia";

export async function test_list_refunds_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. No filters (all refunds, default paging)
  const all: IPageIRefund = await api.functional.paymentsDiscounts.refunds.patch(connection, { body: {} });
  typia.assert<IPageIRefund>(all);
  TestValidator.predicate("pagination present")(() => !!all.pagination && Array.isArray(all.data));
  if (all.data.length > 0) {
    for (const refund of all.data) {
      assertRefundFields(refund);
    }
  }

  // 2. Filter by status (use first if data exists, fallback to stub)
  const status = all.data[0]?.status ?? "requested";
  const byStatus = await api.functional.paymentsDiscounts.refunds.patch(connection, { body: { status } });
  typia.assert<IPageIRefund>(byStatus);
  TestValidator.predicate("all have requested status")(() => byStatus.data.every(r => r.status === status));

  // 3. Filter by payment_id if present
  const payment_id = all.data[0]?.payment_id;
  if (payment_id) {
    const byPayment = await api.functional.paymentsDiscounts.refunds.patch(connection, { body: { payment_id } });
    typia.assert<IPageIRefund>(byPayment);
    TestValidator.predicate("all match payment_id")(() => byPayment.data.every(r => r.payment_id === payment_id));
  }

  // 4. Filter by date range - requested_at
  if (all.data.length > 0) {
    const from = all.data[0].requested_at;
    const to = all.data[Math.min(1, all.data.length-1)]?.requested_at ?? from;
    const byDate = await api.functional.paymentsDiscounts.refunds.patch(connection, { body: { requested_from: from, requested_to: to } });
    typia.assert<IPageIRefund>(byDate);
    for (const r of byDate.data) {
      TestValidator.predicate("in date range")(() => r.requested_at >= from && r.requested_at <= to);
    }
  }

  // 5. Pagination (limit & page)
  const limit = 1;
  const paginated = await api.functional.paymentsDiscounts.refunds.patch(connection, { body: { limit } });
  typia.assert<IPageIRefund>(paginated);
  TestValidator.equals("limited page size")((v: number) => v === limit)(paginated.pagination.limit);

  if (paginated.pagination.pages > 1) {
    const page2 = await api.functional.paymentsDiscounts.refunds.patch(connection, { body: { limit, page: 2 } });
    typia.assert<IPageIRefund>(page2);
    TestValidator.equals("page 2")((v: number) => v === 2)(page2.pagination.current);
  }

  // 6. Edge: Empty result (impossible filter)
  const nothing = await api.functional.paymentsDiscounts.refunds.patch(connection, { body: { status: "___NON_EXISTENT___" } });
  typia.assert<IPageIRefund>(nothing);
  TestValidator.equals("no refunds for nonsense status")([] as IRefund[])(nothing.data);

  // 7. Edge: Invalid page/filters
  await TestValidator.error("invalid negative page")(() => api.functional.paymentsDiscounts.refunds.patch(connection, { body: { page: -1 } }));
  await TestValidator.error("zero limit")(() => api.functional.paymentsDiscounts.refunds.patch(connection, { body: { limit: 0 } }));
  await TestValidator.error("nonsense date range")(() => api.functional.paymentsDiscounts.refunds.patch(connection, { body: { requested_from: "9999-01-01T00:00:00Z", requested_to: "1000-01-01T00:00:00Z" } }));
}

function assertRefundFields(refund: IRefund): void {
  // Audit/evidence minimal fields
  typia.assert<IRefund>(refund);
  TestValidator.predicate("id present")(() => typeof refund.id === "string" && refund.id.length > 0);
  TestValidator.predicate("payment_id present")(() => typeof refund.payment_id === "string" && refund.payment_id.length > 0);
  TestValidator.predicate("amount > 0")(() => typeof refund.amount === "number" && refund.amount >= 0);
  TestValidator.predicate("currency present")(() => typeof refund.currency === "string" && refund.currency.length > 0);
  TestValidator.predicate("status present")(() => typeof refund.status === "string" && refund.status.length > 0);
  TestValidator.predicate("requested_at valid")(() => typeof refund.requested_at === "string" && refund.requested_at.length > 0);
  TestValidator.predicate("transaction_id present")(() => typeof refund.transaction_id === "string" && refund.transaction_id.length > 0);
}

