export async function test_get_refund_by_id_and_invalid_id_errors(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create a refund to test retrieval (happy path)
  const createInput: IRefund.ICreate = typia.random<IRefund.ICreate>();
  const created: IRefund = await api.functional.paymentsDiscounts.refunds.post(
    connection,
    { body: createInput },
  );
  typia.assert<IRefund>(created);

  // Step 2: Fetch by valid ID
  const fetched: IRefund = await api.functional.paymentsDiscounts.refunds.getById(
    connection,
    { id: created.id },
  );
  typia.assert<IRefund>(fetched);
  TestValidator.equals("Fetched refund should match created refund", exceptNullableKeys)(created)(fetched);

  // Step 3: Try fetching with an invalid/non-UUID ID (malformed)
  await TestValidator.httpError("Should throw on invalid UUID format")(
    400,
    422,
  )(async () => {
    await api.functional.paymentsDiscounts.refunds.getById(connection, {
      id: "not-a-uuid" as any,
    });
  });

  // Step 4: Try fetching with a non-existent valid UUID
