// Helper: Ignore fields that may legitimately differ between create/fetch (optional fields, timestamps)
function exceptNullableKeys(key: string): boolean {
  return ["completed_at", "reason", "requested_at", "status"].includes(key);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IRefund } from "@ORGANIZATION/PROJECT-api/lib/structures/IRefund";
import typia from "typia";

export async function test_create_refund_with_metadata_and_idempotency_checks(connection: api.IConnection): Promise<void> {
  // 1. Create valid refund payload
  const validRefund: IRefund.ICreate = {
    payment_id: crypto.randomUUID(),
    transaction_id: crypto.randomUUID(),
    order_id: crypto.randomUUID(),
    amount: 100.0,
    currency: "USD",
    status: "requested",
    reason: "Customer requested refund due to product defect"
  };

  // 2. Happy path: Create the refund
  const refund = await api.functional.paymentsDiscounts.refunds.post(connection, { body: validRefund });
  typia.assert<IRefund>(refund);
  TestValidator.equals("refund.payment_id")(validRefund.payment_id)(refund.payment_id);
  TestValidator.equals("refund.amount")(validRefund.amount)(refund.amount);
  TestValidator.equals("refund.status")(validRefund.status)(refund.status);
  TestValidator.equals("refund.currency")(validRefund.currency)(refund.currency);
  TestValidator.equals("refund.reason")(validRefund.reason)(refund.reason);
  // Should have compliance metadata
  TestValidator.predicate("refund.id is uuid")(() => typeof refund.id === "string" && refund.id.length > 0);
  TestValidator.predicate("refund.requested_at exists")(() => typeof refund.requested_at === "string");

  // 3. Missing required field: payment_id
  const missingPaymentId: IRefund.ICreate = { ...validRefund, payment_id: undefined as any };
  await TestValidator.httpError("missing payment_id")(400)(
    () => api.functional.paymentsDiscounts.refunds.post(connection, { body: missingPaymentId })
  );

  // 4. Negative refund amount
  const negativeAmount: IRefund.ICreate = { ...validRefund, amount: -50 };
  await TestValidator.httpError("negative refund amount")(400, 422)(
    () => api.functional.paymentsDiscounts.refunds.post(connection, { body: negativeAmount })
  );

  // 5. Refunding previously refunded payment (assume payment_id should be unique)
  await TestValidator.httpError("duplicate refund for payment")(400, 409)(
    () => api.functional.paymentsDiscounts.refunds.post(connection, { body: validRefund })
  );

  // 6. Idempotency: Duplicate refund payload
  // For idempotency, the API should not create another refund with same payment_id/transaction_id
  await TestValidator.httpError("idempotent create of refund")(400, 409)(
    () => api.functional.paymentsDiscounts.refunds.post(connection, { body: validRefund })
  );

  // 7. Missing required field: amount
  const missingAmount: IRefund.ICreate = { ...validRefund, amount: undefined as any };
  await TestValidator.httpError("missing amount")(400)(
    () => api.functional.paymentsDiscounts.refunds.post(connection, { body: missingAmount })
  );

  // 8. Missing required field: currency
  const missingCurrency: IRefund.ICreate = { ...validRefund, currency: undefined as any };
  await TestValidator.httpError("missing currency")(400)(
    () => api.functional.paymentsDiscounts.refunds.post(connection, { body: missingCurrency })
  );

  // 9. Missing required field: transaction_id
  const missingTransactionId: IRefund.ICreate = { ...validRefund, transaction_id: undefined as any };
  await TestValidator.httpError("missing transaction_id")(400)(
    () => api.functional.paymentsDiscounts.refunds.post(connection, { body: missingTransactionId })
  );
}

