import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IProductTag } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductTag";
import { IPageIProductTag } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIProductTag";

export async function test_list_and_search_product_tags_with_various_filters(connection: api.IConnection): Promise<void> {
  // 1. Create several distinct tags
  const createdTags: IProductTag[] = [];
  const tagInputs: IProductTag.ICreate[] = [
    { value: "spring-sale" },
    { value: "trending" },
    { value: "AI-suggested" },
  ];
  for (const input of tagInputs) {
    const tag = await api.functional.productCatalog.productTags.post(connection, { body: input });
    typia.assert<IProductTag>(tag);
    createdTags.push(tag);
  }

  // 2. List all tags (no filters)
  const listAll = await api.functional.productCatalog.productTags.patch(connection, { body: {} });
  typia.assert<IPageIProductTag>(listAll);
  const listedIds = listAll.data.map((t) => t.id);
  for (const created of createdTags) {
    TestValidator.predicate(`contains tag with value ${created.value}`)(() => listedIds.includes(created.id));
  }

  // 3. Pagination: limit = 1
  const paged = await api.functional.productCatalog.productTags.patch(connection, { body: { limit: 1 } });
  typia.assert<IPageIProductTag>(paged);
  TestValidator.equals("limit=1 data length")<number>(1)(paged.data.length);
  // Optionally check that returned tag is one of the createdTags
  TestValidator.predicate("paged tag is among created")( () => createdTags.some(c => c.id === paged.data[0]?.id) );

  // 4. Search filter: value = 'trend' (partial match)
  const filtered = await api.functional.productCatalog.productTags.patch(connection, { body: { value: "trend" } });
  typia.assert<IPageIProductTag>(filtered);
  TestValidator.predicate("filtered contains only matching tags")( () =>
    filtered.data.every(tag => tag.value.toLowerCase().includes("trend"))
  );

  // 5. Edge case: search with guaranteed no results
  const none = await api.functional.productCatalog.productTags.patch(connection, { body: { value: "zzz-nomatch-123" } });
  typia.assert<IPageIProductTag>(none);
  TestValidator.equals("empty result for unmatched search")<number>(0)(none.data.length);

  // 6. Edge: invalid/extreme pagination params
  // page = -1 (invalid)
  await TestValidator.httpError("invalid page = -1")(400)(
    () => api.functional.productCatalog.productTags.patch(connection, { body: { page: -1 } })
  );

  // limit = 0
  await TestValidator.httpError("invalid limit = 0")(400)(
    () => api.functional.productCatalog.productTags.patch(connection, { body: { limit: 0 } })
  );

  // extremely large page
  const extremePage = await api.functional.productCatalog.productTags.patch(connection, { body: { page: 100000, limit: 1 } });
  typia.assert<IPageIProductTag>(extremePage);
  // Should return empty data if page surpasses data range
  TestValidator.equals("extremely large page returns empty")(0)(extremePage.data.length);

  // extremely large limit, should return at most all created tags
  const allWithBigLimit = await api.functional.productCatalog.productTags.patch(connection, { body: { limit: 99999 } });
  typia.assert<IPageIProductTag>(allWithBigLimit);
  const createdIds = new Set(createdTags.map(t => t.id));
  TestValidator.predicate("all created tags in big limit page")( () =>
    createdTags.every(tag => allWithBigLimit.data.find(d => d.id === tag.id))
  );
}

