export async function test_update_product_tag_by_id_with_various_conditions(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create an initial product tag
  const created: IProductTag = await api.functional.productCatalog.productTags.post(connection, {
    body: {
      value: `test-tag-${Date.now()}`,
    },
  });
  typia.assert<IProductTag>(created);

  // 2. Update the tag's value (rename)
  const updatedValue = `renamed-tag-${Date.now()}`;
  const updated: IProductTag = await api.functional.productCatalog.productTags.putById(connection, {
    id: created.id,
    body: {
      value: updatedValue,
    },
  });
  typia.assert<IProductTag>(updated);
  TestValidator.equals("rename after update: value matches")<string>(updatedValue)(updated.value);
  TestValidator.equals("id should not change")<string>(created.id)(updated.id);

  // 3. Update with a random (nonexistent) UUID - expect 404
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("update nonexistent product tag should 404")(404)(async () => {
    await api.functional.productCatalog.productTags.putById(connection, {
      id: nonExistentId,
      body: {
        value: "should-not-exist",
      },
    });
  });

  // 4. Update with invalid/missing fields - e.g., empty value
  await TestValidator.error("update should fail if value is empty")(
    async () => {
      await api.functional.productCatalog.productTags.putById(connection, {
        id: created.id,
        body: {
          value: "",
        },
      });
    },
  );

