  await TestValidator.error("update should fail if value missing")(
    async () => {
      await api.functional.productCatalog.productTags.putById(connection, {
        id: created.id,
        body: {},
      });
    },
  );

  // 6. Value uniqueness: create another tag, then try to update first to duplicate
  const another: IProductTag = await api.functional.productCatalog.productTags.post(connection, {
    body: { value: `tag-unique-${Date.now()}` },
  });
  typia.assert<IProductTag>(another);
  await TestValidator.error("update should fail if value not unique")(
    async () => {
      await api.functional.productCatalog.productTags.putById(connection, {
        id: created.id,
        body: { value: another.value },
      });
    },
  );
}

