import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IProductMedia } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductMedia";
import typia from "typia";

export async function test_create_product_media_with_various_data_conditions(connection: api.IConnection): Promise<void> {
  // 1. Valid Product Media Creation
  const validInput: IProductMedia.ICreate = {
    product_id: "84c4f9f1-2f8d-4e8e-bcb2-4dfd1535ca12", // valid UUID
    // product_variant_id intentionally omitted to test only product_id linkage
    uri: "https://cdn.example.com/images/product-123-primary.jpg",
    media_type: "image",
    locale: "en-US",
    alt_text: "Front view of product 123 on white background"
  };
  const created = await api.functional.productCatalog.productMedia.post(connection, { body: validInput });
  typia.assert<IProductMedia>(created);
  TestValidator.equals("product_id should match")(validInput.product_id!)(created.product_id!);
  TestValidator.equals("uri should match")(validInput.uri)(created.uri);
  TestValidator.equals("media_type should match")(validInput.media_type)(created.media_type);
  TestValidator.equals("locale should match")(validInput.locale)(created.locale);
  TestValidator.equals("alt_text should match")(validInput.alt_text)(created.alt_text);
  // created.id and created.created_at should exist - type-checked by typia.assert

  // 2. Creation With Missing Required Fields
  const missingRequiredCases: Partial<IProductMedia.ICreate>[] = [
    // Missing uri
    {
      product_id: validInput.product_id,
      media_type: validInput.media_type,
      locale: validInput.locale,
      alt_text: validInput.alt_text
    },
    // Missing media_type
    {
      product_id: validInput.product_id,
      uri: validInput.uri,
      locale: validInput.locale,
      alt_text: validInput.alt_text
    },
    // Missing locale
    {
      product_id: validInput.product_id,
      uri: validInput.uri,
      media_type: validInput.media_type,
      alt_text: validInput.alt_text
    },
    // Missing alt_text
    {
      product_id: validInput.product_id,
      uri: validInput.uri,
      media_type: validInput.media_type,
      locale: validInput.locale
    },
    // Missing both product_id and product_variant_id (which are alternative required)
    {
      uri: validInput.uri,
      media_type: validInput.media_type,
      locale: validInput.locale,
      alt_text: validInput.alt_text
    }
  ];
  for (const idx in missingRequiredCases) {
    const partial = missingRequiredCases[idx];
    await TestValidator.httpError(`missing required fields case ${idx}`)(400)(
      async () => {
        // Cast to ICreate for call, but will trigger API validation error
        // @ts-expect-error
        return api.functional.productCatalog.productMedia.post(connection, { body: partial as IProductMedia.ICreate });
      }
    );
  }

  // 3. Creation With Invalid Data Types
  const invalidTypeCases: any[] = [
    // Invalid product_id type
    { ...validInput, product_id: 12345 },
    // Invalid uri type
    { ...validInput, uri: 99999 },
    // Invalid media_type type
    { ...validInput, media_type: { wrong: "type" } },
    // Invalid locale type
    { ...validInput, locale: false },
    // Invalid alt_text type
    { ...validInput, alt_text: ["should not be array"] }
  ];
  for (const idx in invalidTypeCases) {
    const invalid = invalidTypeCases[idx];
    await TestValidator.httpError(`invalid data type case ${idx}`)(400)(async () => {
      // @ts-expect-error deliberately sending wrong type
      return api.functional.productCatalog.productMedia.post(connection, { body: invalid });
    });
  }
}

