import { IProductMedia } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductMedia";
import typia, { tags } from "typia";

export async function test_update_product_media_by_id_with_various_conditions(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid product media record
  const createInput: IProductMedia.ICreate = {
    uri: "https://cdn.example.com/media/001.jpg",
    media_type: "image",
    locale: "en-US",
    alt_text: "Product sample image.",
    product_id: typia.random<string & tags.Format<"uuid">>(),
    // product_variant_id is optional
  };
  const media: IProductMedia = await api.functional.productCatalog.productMedia.post(connection, { body: createInput });
  typia.assert<IProductMedia>(media);

  // 2. Valid update: Change alt_text and locale
  const updateInput: IProductMedia.IUpdate = {
    alt_text: "Updated: Accessibility alt text.",
    locale: "ko-KR",
  };
  const updated: IProductMedia = await api.functional.productCatalog.productMedia.putById(connection, {
    id: media.id,
    body: updateInput,
  });
  typia.assert<IProductMedia>(updated);
  TestValidator.equals("alt_text should be updated")<string>(updateInput.alt_text!)(updated.alt_text);
  TestValidator.equals("locale should be updated")<string>(updateInput.locale!)(updated.locale);
  TestValidator.equals("id must not change")<string>(media.id)(updated.id);
  TestValidator.equals("uri remains the same")<string>(media.uri)(updated.uri);

  // 3. Invalid update: missing payload (empty object)
  await TestValidator.httpError("missing attributes triggers validation error")(400)(async () => {
    await api.functional.productCatalog.productMedia.putById(connection, {
      id: media.id,
      body: {},
    } as any); // Purposely invalid type
  });

  // 4. Invalid update: invalid field (wrong type for alt_text)
  await TestValidator.httpError("wrong type triggers validation error")(400)(async () => {
    await api.functional.productCatalog.productMedia.putById(connection, {
      id: media.id,
      body: { alt_text: 1234 },
    } as any);
  });

  // 5. Update non-existent record -> expect 404
  const fakeId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("non-existent record triggers 404")(404)(async () => {
    await api.functional.productCatalog.productMedia.putById(connection, {
      id: fakeId,
      body: { alt_text: "Should not work." },
    });
  });
}

