import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IInventory } from "@ORGANIZATION/PROJECT-api/lib/structures/IInventory";
import { IPageIInventory } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIInventory";

export async function test_list_and_search_inventory_with_various_filters(connection: api.IConnection): Promise<void> {
  // Prepare: create 3 distinct inventory records
  const created: IInventory[] = [];
  for (let i = 0; i < 3; ++i) {
    const createInput: IInventory.ICreate = {
      product_variant_id: typia.random<string & typia.tags.Format<"uuid">>(),
      stock_count: 100 + i * 10,
      last_modified_at: new Date(Date.now() - i * 1000 * 60 * 60).toISOString(),
    };
    const inv = await api.functional.productCatalog.inventory.post(connection, { body: createInput });
    typia.assert<IInventory>(inv);
    created.push(inv);
  }

  // 1. List all inventory: no filters
  const allResult = await api.functional.productCatalog.inventory.patch(connection, { body: {} });
  typia.assert<IPageIInventory>(allResult);
  TestValidator.predicate("all inventory listed")(() => allResult.data.length >= 3);

  // 2. Search by product_variant_id (success)
  const byVariant = await api.functional.productCatalog.inventory.patch(connection, { body: { product_variant_id: created[0].product_variant_id } });
  typia.assert<IPageIInventory>(byVariant);
  TestValidator.predicate("filter by variant returns one result")(() => byVariant.data.length === 1 && byVariant.data[0].product_variant_id === created[0].product_variant_id);

  // 3. Search by non-existent product_variant_id (empty)
  const emptyVariantId = typia.random<string & typia.tags.Format<"uuid">>();
  const emptyResult = await api.functional.productCatalog.inventory.patch(connection, { body: { product_variant_id: emptyVariantId } });
  typia.assert<IPageIInventory>(emptyResult);
  TestValidator.equals("empty result", () => false)([] as IInventory[])(emptyResult.data);

  // 4. Filter by stock_from/stock_to (range covering only second record)
  const secondStock = created[1].stock_count;
  const rangeResult = await api.functional.productCatalog.inventory.patch(connection, { body: { stock_from: secondStock, stock_to: secondStock } });
  typia.assert<IPageIInventory>(rangeResult);
  TestValidator.predicate("range filter single match")(() => rangeResult.data.length === 1 && rangeResult.data[0].stock_count === secondStock);

  // 5. Pagination: limit/offset
  const limitResult = await api.functional.productCatalog.inventory.patch(connection, { body: { limit: 1 } });
  typia.assert<IPageIInventory>(limitResult);
  TestValidator.predicate("pagination limit=1")(() => limitResult.data.length === 1);
  if (allResult.data.length > 1) {
    const offsetResult = await api.functional.productCatalog.inventory.patch(connection, { body: { offset: 1, limit: 1 } });
    typia.assert<IPageIInventory>(offsetResult);
    TestValidator.predicate("pagination offset=1 gives next record")(() => offsetResult.data.length === 1 && offsetResult.data[0].id === allResult.data[1].id);
  }

  // 6. last_modified_at filter: in-range (should match one record)
  const firstModified = created[0].last_modified_at;
  const lastModResult = await api.functional.productCatalog.inventory.patch(connection, { body: { last_modified_from: firstModified, last_modified_to: firstModified } });
  typia.assert<IPageIInventory>(lastModResult);
  TestValidator.predicate("last_modified_at equal filter")(() => lastModResult.data.some(inv => inv.last_modified_at === firstModified));

  // 7. last_modified_at filter: out-of-range (should be empty)
  const oldDate = new Date(2000, 1, 1).toISOString();
  const outRangeResult = await api.functional.productCatalog.inventory.patch(connection, { body: { last_modified_from: oldDate, last_modified_to: oldDate } });
  typia.assert<IPageIInventory>(outRangeResult);
  TestValidator.equals("empty result", () => false)([] as IInventory[])(outRangeResult.data);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { IInventory } from "@ORGANIZATION/PROJECT-api/lib/structures/IInventory";

/**
 * Test fetching inventory by valid and invalid ID: positive and negative flow.
 */
export async function test_get_inventory_by_id_with_valid_and_invalid_id(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Prepare and create a new inventory record
  const createInput: IInventory.ICreate = {
    product_variant_id: typia.random<string & tags.Format<"uuid">>(),
    stock_count: Math.floor(Math.random() * 10) + 1,
    last_modified_at: new Date().toISOString(),
  };

  const created = await api.functional.productCatalog.inventory.post(connection, {
    body: createInput,
  });
  typia.assert<IInventory>(created);
  TestValidator.predicate("Inventory created with correct product_variant_id")(
    created.product_variant_id === createInput.product_variant_id,
  );
  TestValidator.predicate("Inventory created with correct stock_count")(
    created.stock_count === createInput.stock_count,
  );

  // Step 2: Fetch by the existing inventory ID (positive path)
  const fetched = await api.functional.productCatalog.inventory.getById(connection, {
    id: created.id,
  });
  typia.assert<IInventory>(fetched);
  TestValidator.equals("Fetched inventory matches created id")(
    created.id,
  )(fetched.id);
  TestValidator.equals("Fetched product_variant_id matches")(
    created.product_variant_id,
  )(fetched.product_variant_id);
  TestValidator.equals("Fetched stock_count matches")(
    created.stock_count,
  )(fetched.stock_count);
  // server may update last_modified_at, so don't strict-compare

  // Step 3: Negative case - attempt to fetch inventory with non-existent UUID
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("Fetching non-existent inventory returns 404")(404)(async () => {
    await api.functional.productCatalog.inventory.getById(connection, {
      id: nonExistentId,
    });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IInventory } from "@ORGANIZATION/PROJECT-api/lib/structures/IInventory";
import { IProductCatalogProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProductVariant";
import typia from "typia";

export async function test_create_inventory_with_valid_invalid_and_duplicate_data(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid product variant to reference in inventory
  const variantInput: IProductCatalogProductVariant.ICreate = {
    product_id: typia.random<string>(),
    sku: `sku-inventory-test-${Math.random().toString(36).slice(2, 10)}`,
    price: Math.floor(Math.random() * 10000) + 1000,
    barcode: `barcode-${Math.random().toString(36).slice(2, 12)}`,
    available: true,
  };
  const productVariant = await api.functional.productCatalog.productVariants.post(
    connection,
    { body: variantInput },
  );
  typia.assert<IProductCatalogProductVariant>(productVariant);
  TestValidator.predicate("variant id returned")(() => !!productVariant.id);

  // 2. Create valid inventory record
  const now = new Date().toISOString();
  const inventoryInput: IInventory.ICreate = {
    product_variant_id: productVariant.id,
    stock_count: 25,
    last_modified_at: now,
  };
  const inventory = await api.functional.productCatalog.inventory.post(
    connection,
    { body: inventoryInput },
  );
  typia.assert<IInventory>(inventory);
  TestValidator.equals("product_variant_id matches")(
    inventoryInput.product_variant_id,
  )(inventory.product_variant_id);
  TestValidator.equals("stock_count matches")(
    inventoryInput.stock_count,
  )(inventory.stock_count);
  TestValidator.predicate("Has last_modified_at")(
    () => typeof inventory.last_modified_at === "string",
  );
  if (inventory.variant) {
    typia.assert<IProductCatalogProductVariant>(inventory.variant);
    TestValidator.equals("variant relationship")(
      productVariant.id,
    )(inventory.variant.id);
  }

  // 3. Invalid: missing required field (product_variant_id)
  await TestValidator.httpError("missing product_variant_id should fail")(400)(
    () =>
      api.functional.productCatalog.inventory.post(connection, {
        body: {
          // @ts-expect-error: intentionally missing product_variant_id
          stock_count: 31,
          last_modified_at: new Date().toISOString(),
        } as any,
      }),
  );

  // 3b. Invalid: invalid value (negative stock_count)
  await TestValidator.httpError("negative stock_count should fail")(400)(
    () =>
      api.functional.productCatalog.inventory.post(connection, {
        body: {
          product_variant_id: productVariant.id,
          stock_count: -9,
          last_modified_at: new Date().toISOString(),
        },
      }),
  );

  // 4. Duplicate: try to create inventory for the same product_variant
  await TestValidator.httpError("duplicate inventory for same variant should fail")(409, 400)(
    () =>
      api.functional.productCatalog.inventory.post(connection, {
        body: {
          product_variant_id: productVariant.id,
          stock_count: 50,
          last_modified_at: new Date().toISOString(),
        },
      }),
  );
}

