import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IImportFeed } from "@ORGANIZATION/PROJECT-api/lib/structures/IImportFeed";
import { IPageIImportFeed } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIImportFeed";

/**
 * E2E Test: List and search import feed records with various filtering and pagination conditions.
 * Covers both positive and negative cases for PATCH /productCatalog/importFeeds.
 */
export async function test_list_and_search_import_feeds_with_various_conditions(
  connection: api.IConnection
): Promise<void> {
  // --- Step 1: Create several test import feed records ---
  const now = new Date();
  const isoNow = now.toISOString();
  const isoYesterday = new Date(now.getTime() - 24 * 60 * 60 * 1000).toISOString();
  const isoTwoDaysAgo = new Date(now.getTime() - 2 * 24 * 60 * 60 * 1000).toISOString();

  const baseFeeds: IImportFeed.ICreate[] = [
    {
      source_name: "Google Shopping",
      source_uri: `https://feeds.example.com/google?ts=${Date.now()}`,
      imported_at: isoTwoDaysAgo,
    },
    {
      source_name: "Amazon S3",
      source_uri: `https://s3.amazon.com/feed/data/${Date.now() + 1}`,
      imported_at: isoYesterday,
    },
    {
      source_name: "Partner ERP Feed",
      source_uri: `ftp://partner.erp/feeds/${Date.now() + 2}`,
      imported_at: isoNow,
    },
  ];

  // Create records sequentially and store their outputs
  const createdFeeds: IImportFeed[] = [];
  for (const feedInput of baseFeeds) {
    const created: IImportFeed = await api.functional.productCatalog.importFeeds.post(connection, { body: feedInput });
    typia.assert<IImportFeed>(created);
    createdFeeds.push(created);
  }

  // --- Step 2: List all (no filters) ---
  const allFeedsResp: IPageIImportFeed = await api.functional.productCatalog.importFeeds.patch(connection, {
    body: {},
  });
  typia.assert<IPageIImportFeed>(allFeedsResp);
  TestValidator.predicate("all records should be present")(
    () =>
      createdFeeds.every(imported =>
        (allFeedsResp.data || []).some(rec => rec.id === imported.id),
      ),
  );
  TestValidator.predicate("pagination exists")(() => !!allFeedsResp.pagination);

  // --- Step 3: Filter by source_name ---
  const sourceName = baseFeeds[1].source_name;
  const filterBySourceResp = await api.functional.productCatalog.importFeeds.patch(connection, {
    body: { source_name: sourceName },
  });
  typia.assert<IPageIImportFeed>(filterBySourceResp);
  TestValidator.predicate("filter by source_name - match only expected")(
    () =>
      (filterBySourceResp.data || []).every(
        rec => rec.source_name === sourceName,
      ) && (filterBySourceResp.data || []).length === 1,
  );

  // --- Step 4: Filter by imported_at window (just isoYesterday and after) ---
  const windowResp = await api.functional.productCatalog.importFeeds.patch(connection, {
    body: { imported_from: isoYesterday },
  });
  typia.assert<IPageIImportFeed>(windowResp);
  const expectedIds = createdFeeds.filter(f => f.imported_at >= isoYesterday).map(f => f.id);
  TestValidator.predicate("imported_at >= isoYesterday")(
    () =>
      ((windowResp.data || []).length === expectedIds.length &&
        (windowResp.data || []).every(r => expectedIds.includes(r.id)))
  );

  // --- Step 5: String search (partial) by 'Google' (hits source_name) ---
  const searchResp = await api.functional.productCatalog.importFeeds.patch(connection, {
    body: { search: "Google" },
  });
  typia.assert<IPageIImportFeed>(searchResp);
  TestValidator.predicate("string search finds object")(
    () => (searchResp.data || []).some(x => /google/i.test(x.source_name)),
  );

  // --- Step 6: Pagination: set limit = 2, check that paging works ---
  const page1 = await api.functional.productCatalog.importFeeds.patch(connection, { body: { limit: 2, page: 1, sort: "imported_at-asc" } });
  typia.assert<IPageIImportFeed>(page1);
  TestValidator.predicate("pagination: page 1 has 2")(
    () => (page1.data || []).length === 2,
  );
  const page2 = await api.functional.productCatalog.importFeeds.patch(connection, { body: { limit: 2, page: 2, sort: "imported_at-asc" } });
  typia.assert<IPageIImportFeed>(page2);
  TestValidator.predicate("pagination: page 2 has 1 (3 total)")(
    () => (page2.data || []).length === Math.max(0, createdFeeds.length - 2),
  );

  // --- Step 7: Negative - search for source_name that does not exist ---
  const negResp = await api.functional.productCatalog.importFeeds.patch(connection, {
    body: { source_name: "Nonexistent-Feed-Name-XYZ" },
  });
  typia.assert<IPageIImportFeed>(negResp);
  TestValidator.predicate("no results for unknown source_name")(
    () => Array.isArray(negResp.data) && negResp.data.length === 0,
  );
  TestValidator.predicate("negative: still includes pagination block")(
    () => !!negResp.pagination);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { IImportFeed } from "@ORGANIZATION/PROJECT-api/lib/structures/IImportFeed";

export async function test_get_import_feed_by_id_with_valid_and_invalid_id(connection: api.IConnection): Promise<void> {
  // 1. Create a new import feed
  const importFeedInput: IImportFeed.ICreate = {
    source_name: `Test Feed ${Date.now()}`,
    source_uri: `https://test-feed.example.com/file${Math.floor(Math.random() * 1e4)}.csv`,
    // imported_at is optional and omitted for default behavior
  };
  const createdFeed: IImportFeed = await api.functional.productCatalog.importFeeds.post(connection, { body: importFeedInput });
  typia.assert<IImportFeed>(createdFeed);

  // 2. Retrieve the created import feed by ID
  const fetchedFeed: IImportFeed = await api.functional.productCatalog.importFeeds.getById(connection, { id: createdFeed.id });
  typia.assert<IImportFeed>(fetchedFeed);

  // 3. Assert main fields are equal (except for server-generated fields that may differ, e.g., imported_at if omitted on creation)
  TestValidator.equals("source_name")(importFeedInput.source_name)(fetchedFeed.source_name);
  TestValidator.equals("source_uri")(importFeedInput.source_uri)(fetchedFeed.source_uri);
  // imported_at could be auto-set, but should be present in fetchedFeed
  TestValidator.predicate("imported_at present")(() => typeof fetchedFeed.imported_at === "string" && !!fetchedFeed.imported_at);
  TestValidator.equals("id matches")(createdFeed.id)(fetchedFeed.id);

  // 4. Attempt to get a non-existent feed (should error, 404)
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("404 Not Found")(404)(() =>
    api.functional.productCatalog.importFeeds.getById(connection, { id: nonExistentId })
  );
}

