  TestValidator.predicate("created feed has correct source_uri")(() => created.source_uri === validFeed.source_uri);
  if (validFeed.imported_at) {
    TestValidator.predicate("created feed imported_at matches")(() => created.imported_at === validFeed.imported_at);
  }

  // 2. Missing required field: source_name
  await TestValidator.httpError("missing required field: source_name")(400)(async () => {
    const missingSourceName: Partial<IImportFeed.ICreate> = { ...validFeed };
    delete (missingSourceName as any).source_name; // force missing
    // @ts-expect-error testing missing required
    return api.functional.productCatalog.importFeeds.post(connection, { body: missingSourceName });
  });

  // 3. Invalid data format: source_uri too long
  await TestValidator.httpError("source_uri exceeds max length")(400)(async () => {
