      ...validFeed,
      source_uri: "https://" + "a".repeat(80001) + ".example.com"
    };
    return api.functional.productCatalog.importFeeds.post(connection, { body: invalidUri });
  });

  // 4. Attempt duplicate creation (same source_name & source_uri)
  await TestValidator.httpError("duplicate import_feed should fail")(409, 400)(async () => {
    return api.functional.productCatalog.importFeeds.post(connection, { body: validFeed });
  });
}

