import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICatalogEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/ICatalogEvent";
import { IPageICatalogEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICatalogEvent";
import typia from "typia";

function uuidv4() {
  // Dummy UUID generator (to ensure uniqueness for test data)
  return "xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(/[xy]/g, function(c) {
    const r = (Math.random() * 16) | 0,
      v = c == "x" ? r : (r & 0x3) | 0x8;
    return v.toString(16);
  });
}

export async function test_list_and_search_catalog_events_with_various_conditions(
  connection: api.IConnection
): Promise<void> {
  // --- Test Data Setup: Create a variety of events ---
  const baseTime = new Date();
  const user1 = uuidv4();
  const user2 = uuidv4();
  const product1 = uuidv4();
  const variant1 = uuidv4();

  const eventTemplates: ICatalogEvent.ICreate[] = [
    {
      user_id: user1,
      event_type: "create",
      description: "Event create test A",
      occurred_at: new Date(baseTime.getTime() - 100000).toISOString(),
      product_id: product1
    },
    {
      user_id: user2,
      event_type: "update",
      description: "Event update test for B",
      occurred_at: new Date(baseTime.getTime() - 80000).toISOString(),
      product_id: product1,
      product_variant_id: variant1,
    },
    {
      user_id: user1,
      event_type: "status_change",
      description: "Status event",
      occurred_at: new Date(baseTime.getTime() - 60000).toISOString(),
    },
    {
      user_id: user2,
      event_type: "update",
      description: "Another update event, word:Search",
      occurred_at: new Date(baseTime.getTime() - 40000).toISOString(),
    }
  ];
  // Also track responses for assertion
  const createdEvents: ICatalogEvent[] = [];
  for (const e of eventTemplates) {
    const output = await api.functional.productCatalog.catalogEvents.post(connection, { body: e });
    typia.assert<ICatalogEvent>(output);
    createdEvents.push(output);
  }

  // --- Test 1: List all (no filter) ---
  {
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, { body: {} });
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.predicate("all events present")(
      () => createdEvents.every(ev => res.data?.some(e => e.id === ev.id))
    );
  }

  // --- Test 2: Filter by event_type ---
  {
    const eventType = "update";
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, { body: { event_type: eventType } });
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.predicate("event_type filtered correct")(
      () => Array.isArray(res.data) && res.data.every(e => e.event_type === eventType)
    );
  }

  // --- Test 3: Filter by user_id ---
  {
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, { body: { user_id: user1 } });
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.predicate("user_id filtered correct")(
      () => Array.isArray(res.data) && res.data.every(e => e.user_id === user1)
    );
  }

  // --- Test 4: occurred_at window ---
  {
    const from = new Date(baseTime.getTime() - 70000).toISOString();
    const to = new Date(baseTime.getTime() - 30000).toISOString();
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, { body: { from, to }});
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.predicate("occurred_at window")(
      () => Array.isArray(res.data)
        && res.data.every(e => e.occurred_at >= from && e.occurred_at <= to)
    );
  }

  // --- Test 5: Full-text search (description) ---
  {
    const search = "Search";
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, { body: { search } });
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.predicate("full-text description search")(
      () => Array.isArray(res.data)
        && res.data.some(e => (e.description || "").includes(search))
    );
  }

  // --- Test 6: Pagination (limit per page) ---
  {
    const limit = 2;
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, { body: { limit, sort: "occurred_at-asc" } });
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.predicate("pagination respected")(
      () => Array.isArray(res.data) && (!res.data || res.data.length <= limit)
    );
  }

  // --- Test 7: Sort order ---
  {
    const resAsc = await api.functional.productCatalog.catalogEvents.patch(connection, { body: { sort: "occurred_at-asc" } });
    const resDesc = await api.functional.productCatalog.catalogEvents.patch(connection, { body: { sort: "occurred_at-desc" } });
    typia.assert<IPageICatalogEvent>(resAsc);
    typia.assert<IPageICatalogEvent>(resDesc);
    // All occurred_at in asc
    TestValidator.predicate("asc sort correct")(
      () => Array.isArray(resAsc.data) && resAsc.data.every((e, i, arr) => i === 0 || arr[i-1].occurred_at <= e.occurred_at)
    );
    TestValidator.predicate("desc sort correct")(
      () => Array.isArray(resDesc.data) && resDesc.data.every((e, i, arr) => i === 0 || arr[i-1].occurred_at >= e.occurred_at)
    );
  }

  // --- Test 8: Negative (no matches) ---
  {
    const res = await api.functional.productCatalog.catalogEvents.patch(connection, { body: { event_type: "not_a_real_type" } });
    typia.assert<IPageICatalogEvent>(res);
    TestValidator.equals("empty data (no matches)")<ICatalogEvent[]>([])(res.data ?? []);
  }

  // --- Test 9: Negative (invalid filter input) ---
  {
    await TestValidator.httpError("invalid user_id rejects")(400)(() =>
      api.functional.productCatalog.catalogEvents.patch(connection, { body: { user_id: "INVALID-UUID-FORMAT" } })
    );
  }
}

