import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IOrderLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrderLog";
import { IPageIOrderLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIOrderLog";
import typia from "typia";

function makeDate(offset: number): string {
  // ISO-8601 string, offset in minutes from now
  const d = new Date(Date.now() + offset * 60000);
  return d.toISOString();
}

export async function test_list_and_filter_order_logs_with_pagination_and_permissions(
  connection: api.IConnection,
): Promise<void> {
  // 1. Preparation: create order logs with varying attributes
  const baseOrderId = crypto.randomUUID();
  const otherOrderId = crypto.randomUUID();
  const actors = [crypto.randomUUID(), crypto.randomUUID()];
  const eventTypes = ["create", "update", "attach_evidence"];

  const createdLogs: IOrderLog[] = [];
  for (let i = 0; i < 8; ++i) {
    const logInput: IOrderLog.ICreate = {
      order_id: i < 5 ? baseOrderId : otherOrderId,
      event_type: eventTypes[i % eventTypes.length],
      event_time: makeDate(-1000 + i * 10),
      actor_id: actors[i % actors.length],
      event_description: `Event ${i}`,
    };
    const log = await api.functional.cartOrder.orderLog.post(connection, { body: logInput });
    typia.assert<IOrderLog>(log);
    createdLogs.push(log);
  }

  // 2. Filter by order_id, should only get logs for baseOrderId
  {
    const result = await api.functional.cartOrder.orderLog.patch(connection, {
      body: { order_id: baseOrderId }
    });
    typia.assert<IPageIOrderLog>(result);
    TestValidator.predicate("only baseOrderId logs present")(
      result.data.every(l => l.order_id === baseOrderId)
    );
  }

  // 3. Filter by event_type
  {
    const filterType = eventTypes[0];
    const result = await api.functional.cartOrder.orderLog.patch(connection, {
      body: { event_type: filterType }
    });
    typia.assert<IPageIOrderLog>(result);
    TestValidator.predicate("event_type filtered match")(
      result.data.every(l => l.event_type === filterType)
    );
  }

  // 4. Filter by actor_id
  {
    const actor = actors[1];
    const result = await api.functional.cartOrder.orderLog.patch(connection, {
      body: { actor_id: actor }
    });
    typia.assert<IPageIOrderLog>(result);
    TestValidator.predicate("actor_id filtered match")(
      result.data.every(l => l.actor_id === actor)
    );
  }

  // 5. Filter by date range (narrow window -> should match at most 2)
  {
    const from_time = createdLogs[2].event_time;
    const to_time = createdLogs[4].event_time;
    const result = await api.functional.cartOrder.orderLog.patch(connection, {
      body: { from_time, to_time }
    });
    typia.assert<IPageIOrderLog>(result);
    TestValidator.predicate("within date window")(
      result.data.every(l => l.event_time >= from_time && l.event_time < to_time)
    );
  }

  // 6. Pagination: request 3 per page, get correct counts
  {
    const result = await api.functional.cartOrder.orderLog.patch(connection, {
      body: { limit: 3, page: 2 }
    });
    typia.assert<IPageIOrderLog>(result);
    TestValidator.equals("page = 2")(
      2
    )(result.pagination.current);
    TestValidator.equals("limit = 3")(
      3
    )(result.pagination.limit);
    TestValidator.predicate("<= limit items")(
      result.data.length <= 3
    );
  }

  // 7. Empty result: impossible filter
  {
    const result = await api.functional.cartOrder.orderLog.patch(connection, {
      body: { order_id: crypto.randomUUID() }
    });
    typia.assert<IPageIOrderLog>(result);
    TestValidator.equals("empty filter yields []")(
      [] as typeof result.data
    )(result.data);
  }

  // 8. Out-of-range page: returns empty
  {
    const result = await api.functional.cartOrder.orderLog.patch(connection, {
      body: { limit: 2, page: 100 }
    });
    typia.assert<IPageIOrderLog>(result);
    TestValidator.equals("out of range page yields []")(
      [] as typeof result.data
    )(result.data);
  }

  // 9. (Permissions) - Not implemented. Requires auth setup. See note below.
  // If actor impersonation possible, re-run queries as another user, confirm only authorized logs are visible.
}

