export async function test_search_order_evidence_with_pagination_and_authorization(
  connection: api.IConnection,
): Promise<void> {
  // 1. Seed evidence records
  const order_id = crypto.randomUUID();
  const providerA = crypto.randomUUID();
  const providerB = crypto.randomUUID();
  const now = new Date();
  const evidence1_type = "invoice";
  const evidence2_type = "signature";

  // Two with same order & different providers/type
  const ev1 = await api.functional.cartOrder.orderEvidence.post(connection, {
    body: {
      order_id,
      evidence_type: evidence1_type,
      evidence_uri: "https://evidence.local/1",
      provided_at: now.toISOString(),
      provided_by: providerA,
    },
  });
  const ev2 = await api.functional.cartOrder.orderEvidence.post(connection, {
    body: {
      order_id,
      evidence_type: evidence2_type,
      evidence_uri: "https://evidence.local/2",
      provided_at: new Date(now.getTime() + 5000).toISOString(),
      provided_by: providerB,
    },
  });
  // And another with different order, tested for negative filter
  const another_order_id = crypto.randomUUID();
  const ev3 = await api.functional.cartOrder.orderEvidence.post(connection, {
    body: {
      order_id: another_order_id,
      evidence_type: "receipt",
      evidence_uri: "https://evidence.local/3",
      provided_at: new Date(now.getTime() + 10000).toISOString(),
      provided_by: providerA,
    },
  });

  // 2a. No filter - should return at least our 3
  const allResults = await api.functional.cartOrder.orderEvidence.patch(connection, {
    body: {},
  });
  typia.assert<IPageIOrderEvidence>(allResults);
  TestValidator.predicate("Should have seeded records in results")(
    () => allResults.data.some((ev) => ev.id === ev1.id && ev.order_id === order_id)
  );
  TestValidator.predicate("Pagination meta looks valid")(
    () => allResults.pagination.records >= 3 && allResults.pagination.current === 1
  );

  // 2b. Filter by order_id
  const byOrder = await api.functional.cartOrder.orderEvidence.patch(connection, {
    body: { order_id },
  });
  typia.assert<IPageIOrderEvidence>(byOrder);
  TestValidator.predicate("All data matches order_id")(
    () => byOrder.data.every((ev) => ev.order_id === order_id)
  );
  TestValidator.predicate("Should find at least 2 with this order_id")(
    () => byOrder.data.length >= 2
  );

  // 2c. Filter by evidence_type
  const byType = await api.functional.cartOrder.orderEvidence.patch(connection, {
    body: { evidence_type: evidence2_type },
  });
  typia.assert<IPageIOrderEvidence>(byType);
  TestValidator.predicate("All data matches evidence_type")(
    () => byType.data.every((ev) => ev.evidence_type === evidence2_type)
  );
  TestValidator.predicate("Should find at least 1 by evidence_type")(
    () => byType.data.some((ev) => ev.id === ev2.id)
  );

  // 2d. Pagination: limit=1, page=2
  const page2 = await api.functional.cartOrder.orderEvidence.patch(connection, {
    body: { limit: 1, page: 2 },
  });
  typia.assert<IPageIOrderEvidence>(page2);
  TestValidator.predicate("Limit=1 yields one record, page=2 moves page")(
    () => page2.data.length === 1 && page2.pagination.current === 2
  );

  // 2e. Invalid filter (non-existent order_id)
  const nohit = await api.functional.cartOrder.orderEvidence.patch(connection, {
    body: { order_id: crypto.randomUUID() },
  });
  typia.assert<IPageIOrderEvidence>(nohit);
  TestValidator.equals("Empty result on bad filter")(<IOrderEvidence[]>[])(nohit.data);

  // 4. Authorization checks (simulate by stripping headers)
  const unauthConnection = { ...connection, headers: {} };
  await TestValidator.httpError("Missing auth yields 401/403")(401, 403)(
    () => api.functional.cartOrder.orderEvidence.patch(unauthConnection, { body: {} })
  );
}

