import { IOrderEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrderEvidence";

export async function test_update_order_evidence_with_valid_and_invalid_modifications(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new order_evidence record (setup)
  const createInput: IOrderEvidence.ICreate = typia.random<IOrderEvidence.ICreate>();
  const created: IOrderEvidence = await api.functional.cartOrder.orderEvidence.post(connection, { body: createInput });
  typia.assert(created);

  // 2. Valid update: change metadata (evidence_type, evidence_uri, provided_by)
  const validUpdate: IOrderEvidence.IUpdate = {
    evidence_type: created.evidence_type + "_upd",
    evidence_uri: created.evidence_uri + "?v=2",
    provided_by: created.provided_by ?? createInput.provided_by ?? null,
  };
  const updated = await api.functional.cartOrder.orderEvidence.putById(connection, {
    id: created.id,
    body: validUpdate,
  });
  typia.assert<IOrderEvidence>(updated);
  TestValidator.equals("evidence_type updated")(validUpdate.evidence_type)(updated.evidence_type);
  TestValidator.equals("evidence_uri updated")(validUpdate.evidence_uri)(updated.evidence_uri);
  TestValidator.equals("provided_by updated")(
    validUpdate.provided_by ?? null
  )(updated.provided_by ?? null);

  // 3. Invalid update: evidence_type empty string (should fail)
  const invalidTypeUpdate: IOrderEvidence.IUpdate = {
    ...validUpdate,
    evidence_type: "",
  };
  await TestValidator.httpError("evidence_type empty, should fail")(400)(async () =>
    api.functional.cartOrder.orderEvidence.putById(connection, {
      id: created.id,
      body: invalidTypeUpdate,
    })
  );

  // 4. Invalid update: evidence_uri excessively long (should fail)
  const longUri = "a".repeat(80001); // Exceeds MaxLength<80000>
  const invalidUriUpdate: IOrderEvidence.IUpdate = {
    ...validUpdate,
    evidence_uri: longUri,
  };
  await TestValidator.httpError("evidence_uri too long, should fail")(400)(async () =>
    api.functional.cartOrder.orderEvidence.putById(connection, {
      id: created.id,
      body: invalidUriUpdate,
    })
  );

  // 5. Update non-existent ID (should fail, e.g., 404)
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("update non-existent ID, should 404")(404)(async () =>
    api.functional.cartOrder.orderEvidence.putById(connection, {
      id: nonExistentId,
      body: validUpdate,
    })
  );
}

