export async function test_soft_delete_order_evidence_and_assert_audit_trail(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid evidence record
  const createInput: IOrderEvidence.ICreate = typia.random<IOrderEvidence.ICreate>();
  const created: IOrderEvidence = await api.functional.cartOrder.orderEvidence.post(connection, { body: createInput });
  typia.assert<IOrderEvidence>(created);
  TestValidator.equals("created.order_id matches input")<string>()(createInput.order_id)(created.order_id);
  TestValidator.equals("created.evidence_type matches input")<string>()(createInput.evidence_type)(created.evidence_type);
  TestValidator.equals("created.evidence_uri matches input")<string>()(createInput.evidence_uri)(created.evidence_uri);
  TestValidator.equals("created.provided_at matches input")<string>()(createInput.provided_at)(created.provided_at);
  // provided_by is optional
  TestValidator.equals("created.provided_by matches input")<string | null | undefined>()(
