export async function test_search_and_paginate_payments_with_various_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Seed several payment records (simulate three users with different scenarios).
  const userA = typia.random<string & typia.tags.Format<"uuid">>();
  const userB = typia.random<string & typia.tags.Format<"uuid">>();
  const userC = typia.random<string & typia.tags.Format<"uuid">>();
  const methodA = typia.random<string & typia.tags.Format<"uuid">>();
  const methodB = typia.random<string & typia.tags.Format<"uuid">>();

  // Use predictable dates for filtering.
  const now = new Date();
  const yesterday = new Date(now.getTime() - 24 * 60 * 60 * 1000);
  const twoDaysAgo = new Date(now.getTime() - 2 * 24 * 60 * 60 * 1000);
  const toISOString = (d: Date) => d.toISOString();

  // Seed payment1: userA, completed, methodA, USD
  const payment1 = await api.functional.paymentsDiscounts.payments.post(connection, {
    body: {
      transaction_id: typia.random<string & typia.tags.Format<"uuid">>(),
      payment_method_id: methodA,
      amount: 100.5,
      currency: "USD",
      description: "Payment 1 - A, completed",
    },
  });

  // Seed payment2: userB, pending, methodB, KRW
  const payment2 = await api.functional.paymentsDiscounts.payments.post(connection, {
    body: {
      transaction_id: typia.random<string & typia.tags.Format<"uuid">>(),
      payment_method_id: methodB,
      amount: 200.0,
      currency: "KRW",
      description: "Payment 2 - B, pending",
    },
  });

  // Seed payment3: userA, refunded, methodA
  const payment3 = await api.functional.paymentsDiscounts.payments.post(connection, {
    body: {
      transaction_id: typia.random<string & typia.tags.Format<"uuid">>(),
      payment_method_id: methodA,
      amount: 50,
      currency: "USD",
      description: "Payment 3 - A, refunded",
    },
  });

  // Set known user_id and status for reference (simulate with patch if endpoint supported; assume insert returns association)
  // We'll use what we have. For filter: use e.g. payment_method_id, currency, and create-time window

  // 2a. Pagination (limit = 2, page = 1)
  const paged = await api.functional.paymentsDiscounts.payments.patch(connection, {
    body: {
      limit: 2,
      page: 1,
    },
  });
  typia.assert<IPageIPayment>(paged);
  TestValidator.predicate("Page size correct")(
    paged.data.length <= 2 && paged.pagination.limit === 2
  );

  // 2b. Filter by payment_method_id (methodA)
  const methodAList = await api.functional.paymentsDiscounts.payments.patch(connection, {
    body: {
      payment_method_id: methodA,
    },
  });
  typia.assert<IPageIPayment>(methodAList);
  TestValidator.predicate("Only methodA payments present")(
    methodAList.data.every((p) => p.payment_method_id === methodA)
  );

  // 2c. Filter by currency (KRW)
  const krwList = await api.functional.paymentsDiscounts.payments.patch(connection, {
    body: {
      currency: "KRW",
    },
  });
  typia.assert<IPageIPayment>(krwList);
  TestValidator.predicate("Only KRW payments present")(
    krwList.data.every((p) => p.currency === "KRW")
  );

  // 2d. Filter by initiated_at date range (from twoDaysAgo to now)
  const dateRangeList = await api.functional.paymentsDiscounts.payments.patch(connection, {
    body: {
      initiated_from: toISOString(twoDaysAgo),
      initiated_to: toISOString(now),
    },
  });
  typia.assert<IPageIPayment>(dateRangeList);
  TestValidator.predicate("Payments in date range")(
    dateRangeList.data.every(
      (p) => p.initiated_at >= toISOString(twoDaysAgo) && p.initiated_at < toISOString(now),
    )
  );

  // 2e. Filter with status (try status from payment1)
  const status = payment1.status;
  const statusList = await api.functional.paymentsDiscounts.payments.patch(connection, {
    body: {
      status,
    },
  });
  typia.assert<IPageIPayment>(statusList);
  TestValidator.predicate("All payments have status")(
    statusList.data.every((p) => p.status === status)
  );

  // 2f. No result (absent status)
  const noneList = await api.functional.paymentsDiscounts.payments.patch(connection, {
    body: {
      status: "NEVER_USED_STATUS",
    },
  });
  typia.assert<IPageIPayment>(noneList);
  TestValidator.equals("No results for unknown status")([] as IPayment[])(noneList.data);

  // 3. Invalid filter (invalid date string)
  await TestValidator.error("Invalid date string handled")(
    async () =>
      await api.functional.paymentsDiscounts.payments.patch(connection, {
        body: {
          initiated_from: "not-a-date",
        },
      })
  );
}

