import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPayment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPayment";
import { ICartOrderOrder } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderOrder";
import typia from "typia";

function generateUUID(): string {
  // Simple RFC4122 v4 pattern for mock (since no uuid API)
  return "xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(/[xy]/g, c => {
    const r = (Math.random() * 16) | 0,
      v = c == "x" ? r : (r & 0x3) | 0x8;
    return v.toString(16);
  });
}

export async function test_create_payment_with_all_permutations_of_valid_and_invalid_input(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Prepare prerequisite valid order (simulate transaction context)
  const user_id = generateUUID();
  const cart_id = generateUUID();
  const order = await api.functional.cartOrder.order.post(connection, {
    body: {
      user_id,
      cart_id,
    },
  });

  typia.assert<ICartOrderOrder>(order);

  // We'll mock a transaction_id and payment_method_id as UUIDs (since no APIs)
  const transaction_id = generateUUID();
  const payment_method_id = generateUUID();

  // Step 2: Positive - Valid Payment Creation
  const validPayment: IPayment.ICreate = {
    transaction_id,
    payment_method_id,
    amount: 199.99,
    currency: "USD",
    description: "Test payment order linkage",
  };

  const payment = await api.functional.paymentsDiscounts.payments.post(connection, {
    body: validPayment,
  });
  typia.assert<IPayment>(payment);
  TestValidator.equals("transaction_id matches")(validPayment.transaction_id)(payment.transaction_id);
  TestValidator.equals("payment_method_id matches")(validPayment.payment_method_id)(payment.payment_method_id);
  TestValidator.equals("amount matches")(validPayment.amount)(payment.amount);
  TestValidator.equals("currency matches")(validPayment.currency)(payment.currency);
  if (validPayment.description)
    TestValidator.equals("description matches")(validPayment.description)(payment.description);

  // Step 3: Negative - Missing Required Fields
  const requiredFields: (keyof IPayment.ICreate)[] = [
    "transaction_id",
    "payment_method_id",
    "amount",
    "currency",
  ];
  for (const field of requiredFields) {
    const invalid: IPayment.ICreate = { ...validPayment };
    // @ts-expect-error
    delete invalid[field];
    await TestValidator.error(`missing required field: ${field}`)(async () =>
      api.functional.paymentsDiscounts.payments.post(connection, {
        body: invalid,
      }),
    );
  }

  // Step 4: Negative - Invalid References (random UUID means not in DB)
  const invalidRef: IPayment.ICreate = {
    ...validPayment,
    transaction_id: generateUUID(),
    payment_method_id: generateUUID(),
  };
  await TestValidator.error("invalid transaction_id & payment_method_id")(() =>
    api.functional.paymentsDiscounts.payments.post(connection, {
      body: invalidRef,
    })
  );

  // Step 5: Negative - Edge Values for amount/currency
  const invalidAmounts = [0, -1, -1000];
  for (const amount of invalidAmounts) {
    const payload: IPayment.ICreate = { ...validPayment, amount };
    await TestValidator.error(`invalid amount: ${amount}`)(() =>
      api.functional.paymentsDiscounts.payments.post(connection, {
        body: payload,
      })
    );
  }
  const badCurrencies = ["", "US", "USDEUR", "KRWKRW"];
  for (const currency of badCurrencies) {
    const payload: IPayment.ICreate = { ...validPayment, currency };
    await TestValidator.error(`invalid currency: ${currency}`)(() =>
      api.functional.paymentsDiscounts.payments.post(connection, {
        body: payload,
      })
    );
  }

  // Step 6: Duplicate/Idempotency: Retry same payload (business rules may vary)
  await TestValidator.error("duplicate payment creation")(() =>
    api.functional.paymentsDiscounts.payments.post(connection, {
      body: validPayment,
    })
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPayment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPayment";
import typia, { tags } from "typia";

export async function test_update_payment_metadata_and_enforce_integrity(connection: api.IConnection): Promise<void> {
  // 1. Seed with a valid payment
  const createInput: IPayment.ICreate = typia.random<IPayment.ICreate>();
  const created: IPayment = await api.functional.paymentsDiscounts.payments.post(connection, { body: createInput });
  typia.assert(created);

  // 2. Perform a valid metadata update
  const updateInput: IPayment.IUpdate = {
    description: "Updated for audit test",
    completed_at: new Date().toISOString(), // simulate completion
    // Optionally, could update payment_method_id as well
  };

  const updated: IPayment = await api.functional.paymentsDiscounts.payments.putById(connection, {
    id: created.id,
    body: updateInput,
  });
  typia.assert(updated);

  // 3. Validate allowed/mutated fields and unchanged immutables
  TestValidator.equals("id should be unchanged")(created.id)(updated.id);
  TestValidator.equals("transaction_id should be unchanged")(created.transaction_id)(updated.transaction_id);
  TestValidator.equals("amount should be unchanged")(created.amount)(updated.amount);
  TestValidator.equals("currency should be unchanged")(created.currency)(updated.currency);
  TestValidator.equals("status should be unchanged")(created.status)(updated.status);
  TestValidator.equals("initiated_at should be unchanged")(created.initiated_at)(updated.initiated_at);

  if (updateInput.completed_at !== undefined) {
    TestValidator.equals("completed_at should update")(updateInput.completed_at!)(updated.completed_at!);
  }
  if (updateInput.description !== undefined) {
    TestValidator.equals("description should update")(updateInput.description!)(updated.description!);
  }

  // 4. Attempt to update immutable fields: amount
  await TestValidator.httpError("should not allow update of immutable fields - amount")(400, 404, 422, 403)(async () =>
    api.functional.paymentsDiscounts.payments.putById(connection, {
      id: created.id,
      body: { ...updateInput, amount: (created.amount || 1) + 100 },
    } as any),
  );

  // 5. Attempt to update a non-existent payment
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("should error for non-existent payment")(404, 400)(async () =>
    api.functional.paymentsDiscounts.payments.putById(connection, {
      id: nonExistentId,
      body: updateInput,
    }),
  );
}

