import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPaymentMethod } from "@ORGANIZATION/PROJECT-api/lib/structures/IPaymentMethod";
import typia from "typia";

/**
 * Test creating payment methods, with validation and duplicate check.
 */
export async function test_create_payment_method_with_validation_and_duplicate_check(
  connection: api.IConnection,
): Promise<void> {
  // 1. Prepare valid input
  const validInput: IPaymentMethod.ICreate = {
    user_id: crypto.randomUUID(),
    type: "VISA",
    masked_detail: "**** **** **** 1234",
    provider: "Stripe",
    active: true,
  };

  // 2. POST with valid input
  const created = await api.functional.paymentsDiscounts.paymentMethods.post(connection, { body: validInput });
  typia.assert<IPaymentMethod>(created);
  TestValidator.equals("user_id should match")(validInput.user_id)(created.user_id);
  TestValidator.equals("type should match")(validInput.type)(created.type);
  TestValidator.equals("active status match")(validInput.active)(created.active);
  TestValidator.predicate("created has id")(() => typeof created.id === "string" && created.id.length > 0);
  TestValidator.predicate("created has masked_detail")(() => created.masked_detail === validInput.masked_detail);

  // 3. Post with missing required fields (user_id)
  await TestValidator.httpError("missing user_id should fail")(400)(() =>
    api.functional.paymentsDiscounts.paymentMethods.post(connection, {
      body: {
        // @ts-expect-error
        type: "VISA",
        masked_detail: "**** **** **** 3456",
        provider: "Stripe",
        active: true,
      },
    })
  );

  // Missing masked_detail
  await TestValidator.httpError("missing masked_detail should fail")(400)(() =>
    api.functional.paymentsDiscounts.paymentMethods.post(connection, {
      body: {
        user_id: validInput.user_id,
        type: "VISA",
        // @ts-expect-error
        provider: "Stripe",
        active: true,
      },
    })
  );

  // Missing type
  await TestValidator.httpError("missing type should fail")(400)(() =>
    api.functional.paymentsDiscounts.paymentMethods.post(connection, {
      body: {
        user_id: validInput.user_id,
        masked_detail: "**** **** **** 3456",
        provider: "Stripe",
        active: true,
        // @ts-expect-error
      },
    })
  );

  // 4. Post a duplicate (same data)
  await TestValidator.httpError("duplicate insertion should fail")(409)(() =>
    api.functional.paymentsDiscounts.paymentMethods.post(connection, { body: validInput })
  );

  // 5. Create a different method for the same user (different type/masked)
  const newMethod: IPaymentMethod.ICreate = {
    user_id: validInput.user_id,
    type: "MASTERCARD",
    masked_detail: "**** **** **** 8888",
    provider: "Stripe",
    active: true,
  };
  const created2 = await api.functional.paymentsDiscounts.paymentMethods.post(connection, { body: newMethod });
  typia.assert<IPaymentMethod>(created2);
  TestValidator.equals("should have a new id")(false)(created.id === created2.id);
  TestValidator.equals("should use same user_id")(validInput.user_id)(created2.user_id);
  TestValidator.equals("should use new type")(newMethod.type)(created2.type);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";
import { IPaymentMethod } from "@ORGANIZATION/PROJECT-api/lib/structures/IPaymentMethod";

export async function test_update_payment_method_fields_and_permissions(connection: api.IConnection): Promise<void> {
  // 1. Create a valid payment method to update
  const createInput: IPaymentMethod.ICreate = {
    user_id: typia.random<string & tags.Format<"uuid">>(),
    type: "VISA",
    masked_detail: "1234",
    active: true,
    provider: "TestProvider",
  };
  const created: IPaymentMethod = await api.functional.paymentsDiscounts.paymentMethods.post(connection, { body: createInput });
  typia.assert<IPaymentMethod>(created);

  // 2. Positive update: Update masked_detail
  const newMaskedDetail = "5678";
  const updated1 = await api.functional.paymentsDiscounts.paymentMethods.putById(connection, {
    id: created.id,
    body: { masked_detail: newMaskedDetail },
  });
  typia.assert<IPaymentMethod>(updated1);
  TestValidator.equals("masked_detail updated")(newMaskedDetail)(updated1.masked_detail);
  TestValidator.equals("provider unchanged")(created.provider)(updated1.provider);
  TestValidator.equals("user_id unchanged")(created.user_id)(updated1.user_id);
  TestValidator.equals("type unchanged")(created.type)(updated1.type);

  // 2. Positive update: Update provider
  const newProvider = "AnotherProvider";
  const updated2 = await api.functional.paymentsDiscounts.paymentMethods.putById(connection, {
    id: created.id,
    body: { provider: newProvider },
  });
  typia.assert<IPaymentMethod>(updated2);
  TestValidator.equals("provider updated")(newProvider)(updated2.provider);
  TestValidator.equals("masked_detail unchanged")(updated1.masked_detail)(updated2.masked_detail);
  TestValidator.equals("user_id unchanged")(created.user_id)(updated2.user_id);
  TestValidator.equals("type unchanged")(created.type)(updated2.type);

  // 2. Positive update: Toggle active
  const updated3 = await api.functional.paymentsDiscounts.paymentMethods.putById(connection, {
    id: created.id,
    body: { active: false },
  });
  typia.assert<IPaymentMethod>(updated3);
  TestValidator.equals("active field updated")(false)(updated3.active);
  TestValidator.equals("user_id unchanged")(created.user_id)(updated3.user_id);
  TestValidator.equals("type unchanged")(created.type)(updated3.type);

  // 2. All updatable fields together
  const updatedAll = await api.functional.paymentsDiscounts.paymentMethods.putById(connection, {
    id: created.id,
    body: {
      masked_detail: "9999",
      active: true,
      provider: "BulkProvider"
    },
  });
  typia.assert<IPaymentMethod>(updatedAll);
  TestValidator.equals("all fields updated")("9999")(updatedAll.masked_detail);
  TestValidator.equals("all fields updated")(true)(updatedAll.active);
  TestValidator.equals("all fields updated")("BulkProvider")(updatedAll.provider);

  // 3. Negative: Update non-existent payment method
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("update non-existent method")(404)(async () => {
    await api.functional.paymentsDiscounts.paymentMethods.putById(connection, {
      id: nonExistentId,
      body: { masked_detail: "notfound" },
    });
  });

  // 4. Forbidden update attempts: DTO-based API so non-mutable fields can't be changed; simulate by casting
  // Attempt to update 'type' (not allowed by DTO)
  const forbiddenPatch: any = { type: "HACKED" };
  // Should be ignored or cause error, but allow execution for robustness
  const forbiddenResult = await api.functional.paymentsDiscounts.paymentMethods.putById(connection, {
    id: created.id,
    body: forbiddenPatch as IPaymentMethod.IUpdate,
  });
  typia.assert<IPaymentMethod>(forbiddenResult);
  TestValidator.equals("type field is unchanged after forbidden update")(created.type)(forbiddenResult.type);

  // Attempt to update user_id (not allowed by DTO)
  const forbiddenPatch2: any = { user_id: typia.random<string & tags.Format<"uuid">>() };
  const forbiddenResult2 = await api.functional.paymentsDiscounts.paymentMethods.putById(connection, {
    id: created.id,
    body: forbiddenPatch2 as IPaymentMethod.IUpdate,
  });
  typia.assert<IPaymentMethod>(forbiddenResult2);
  TestValidator.equals("user_id field is unchanged after forbidden update")(created.user_id)(forbiddenResult2.user_id);
}

