import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IDiscount } from "@ORGANIZATION/PROJECT-api/lib/structures/IDiscount";
import { IPageIDiscount } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIDiscount";

export async function test_search_and_filter_discounts_with_rules_and_status(
  connection: api.IConnection,
): Promise<void> {
  // Helper to create discount and collect its params
  const discounts: IDiscount[] = [];

  // Unique guids for rules, orders, and simulate campaigns
  const ruleA = typia.random<string & typia.tags.Format<"uuid">>();
  const ruleB = typia.random<string & typia.tags.Format<"uuid">>();
  const orderA = typia.random<string & typia.tags.Format<"uuid">>();
  const orderB = typia.random<string & typia.tags.Format<"uuid">>();
  const campaignId = typia.random<string & typia.tags.Format<"uuid">>();

  // -- Create discounts (simulate various attributes)
  const d1 = await api.functional.paymentsDiscounts.discounts.post(connection, { body: { discount_rule_id: ruleA, order_id: orderA, amount: -5000, notes: "control: active no-campaign" } });
  discounts.push(d1);
  const d2 = await api.functional.paymentsDiscounts.discounts.post(connection, { body: { discount_rule_id: ruleA, order_id: orderB, amount: -2500, notes: "campaign-x", } });
  discounts.push(d2);
  const d3 = await api.functional.paymentsDiscounts.discounts.post(connection, { body: { discount_rule_id: ruleB, order_id: orderA, amount: -12000, notes: "large discount", } });
  discounts.push(d3);
  // Simulate one with a campaign and mock campaign_id filter effect
  const d4 = await api.functional.paymentsDiscounts.discounts.post(connection, { body: { discount_rule_id: ruleB, order_id: orderB, amount: -7000, notes: `campaign:${campaignId}`, } });
  discounts.push(d4);

  // (If 'status' is not a field, skip the status=disabled test)

  // Pagination test: expect at least 2 discounts
  let pageResp = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { limit: 2, page: 1 },
  });
  typia.assert<IPageIDiscount>(pageResp);
  TestValidator.predicate("pagination length")(pageResp.data.length <= 2);
  TestValidator.predicate("pagination pages valid")(pageResp.pagination.pages >= 1);

  // Filter by discount_rule_id
  pageResp = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { discount_rule_id: ruleA },
  });
  typia.assert<IPageIDiscount>(pageResp);
  TestValidator.predicate("ruleA filter all ruleA")(() => pageResp.data.every((d) => d.discount_rule_id === ruleA));

  // Filter by campaign (simulate through notes string matching since no campaign_id on record)
  pageResp = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { campaign_id: campaignId },
  });
  typia.assert<IPageIDiscount>(pageResp);
  // Can't guarantee implementation - at least ensure if campaign-linked, correct note exists
  TestValidator.predicate("campaignId filter")(() => pageResp.data.every((d) => (d.notes || "").includes(campaignId)));

  // Date applied_from filter (all discounts are fresh, so expect all returned from now-1hr)
  const hourAgo = new Date(Date.now() - 60 * 60 * 1000).toISOString();
  pageResp = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { applied_from: hourAgo },
  });
  typia.assert<IPageIDiscount>(pageResp);
  TestValidator.predicate("all after hourAgo")(() => pageResp.data.length >= 1);

  // Amount filters min/max
  pageResp = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { amount_from: -8000, amount_to: -2000 },
  });
  typia.assert<IPageIDiscount>(pageResp);
  TestValidator.predicate("amount range")(() => pageResp.data.every((d) => d.amount >= -8000 && d.amount < -2000));

  // Edge: filter matches no records
  pageResp = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { order_id: typia.random<string & typia.tags.Format<"uuid">>() },
  });
  typia.assert<IPageIDiscount>(pageResp);
  TestValidator.equals("empty data [])")([] as IDiscount[])(pageResp.data);

  // Malformed input: amount_from greater than amount_to
  await TestValidator.httpError("amount filter reverse should error")(400)(async () => {
    await api.functional.paymentsDiscounts.discounts.patch(connection, {
      body: { amount_from: 10, amount_to: -10 } as any,
    });
  });

  // Very high page number
  pageResp = await api.functional.paymentsDiscounts.discounts.patch(connection, {
    body: { page: 1000 },
  });
  typia.assert<IPageIDiscount>(pageResp);
  TestValidator.equals("very high page yields empty[]")([] as IDiscount[])(pageResp.data);
}

