  return "b7eaaab4-1234-4fa3-8888-" + Math.floor(Math.random() * 90000 + 10000);
}

export async function test_create_discount_with_rule_assignment_and_invalid_data_handling(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create two discount rules for use in further tests
  const discountRule1: IDiscountRule = await api.functional.paymentsDiscounts.discountRules.post(
