  // 2. Create a minimal discount (required fields only)
  const orderId1 = randomUUID();
  const discountInputMinimal: IDiscount.ICreate = {
    discount_rule_id: discountRule1.id,
    order_id: orderId1,
    amount: -1000,
  };
  const discountMinimal = await api.functional.paymentsDiscounts.discounts.post(
    connection,
    { body: discountInputMinimal },
  );
  typia.assert<IDiscount>(discountMinimal);
  TestValidator.equals("discount_rule_id")(discountInputMinimal.discount_rule_id)(discountMinimal.discount_rule_id);
  TestValidator.equals("order_id")(discountInputMinimal.order_id)(discountMinimal.order_id);
  TestValidator.equals("amount")(discountInputMinimal.amount)(discountMinimal.amount);

  // 3. Create a full discount (with notes)
  const orderId2 = randomUUID();
  const discountInputFull: IDiscount.ICreate = {
    discount_rule_id: discountRule2.id,
    order_id: orderId2,
    amount: -2000,
    notes: "AI campaign test",
  };
  const discountFull = await api.functional.paymentsDiscounts.discounts.post(
    connection,
    { body: discountInputFull },
  );
  typia.assert<IDiscount>(discountFull);
  TestValidator.equals("notes")(discountInputFull.notes)(discountFull.notes);

  // 4. Negative: missing required field (no order_id)
  await TestValidator.httpError("Missing order_id should fail")(400)(async () => {
    const invalidInput = {
      discount_rule_id: discountRule1.id,
      amount: -500,
    } as any;
    await api.functional.paymentsDiscounts.discounts.post(connection, { body: invalidInput });
  });

  // 5. Negative: invalid UUID value for order_id
  await TestValidator.httpError("Invalid UUID in order_id")(400)(async () => {
    const invalidUUIDInput: IDiscount.ICreate = {
      discount_rule_id: discountRule1.id,
      order_id: "not-a-uuid",
      amount: -400,
    };
    await api.functional.paymentsDiscounts.discounts.post(connection, { body: invalidUUIDInput });
  });

  // 6. Negative: duplicate discount for same rule/order
  await TestValidator.error("Duplicate discount should be rejected")(async () => {
    await api.functional.paymentsDiscounts.discounts.post(connection, { body: discountInputMinimal });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";
import { IDiscount } from "@ORGANIZATION/PROJECT-api/lib/structures/IDiscount";

export async function test_update_discount_with_valid_fields_and_prevent_immutable_mutation(connection: api.IConnection): Promise<void> {
    // 1. Create a discount for update testing
    const discountCreate: IDiscount.ICreate = typia.random<IDiscount.ICreate>();
    const created: IDiscount = await api.functional.paymentsDiscounts.discounts.post(connection, { body: discountCreate });
    typia.assert<IDiscount>(created);

    // 2. Update mutable fields (amount, notes)
    const updateBody: IDiscount.IUpdate = {
        amount: (created.amount || 0) - 1000, // attempt to change the amount by reducing 1000 (if it is allowed)
        notes: "Updated discount note for compliance test"
    };
    const updated: IDiscount = await api.functional.paymentsDiscounts.discounts.putById(connection, {
        id: created.id,
        body: updateBody,
    });
    typia.assert<IDiscount>(updated);
    // Mutable fields should be updated
    if (typeof updateBody.amount === "number")
        TestValidator.equals("amount should update")(updateBody.amount)(updated.amount);
    if (typeof updateBody.notes !== "undefined")
        TestValidator.equals("notes should update")(updateBody.notes)(updated.notes);
    // Immutable fields should not be affected
    TestValidator.equals("id is immutable")(created.id)(updated.id);
    TestValidator.equals("discount_rule_id is immutable")(created.discount_rule_id)(updated.discount_rule_id);
    TestValidator.equals("order_id is immutable")(created.order_id)(updated.order_id);
    TestValidator.equals("applied_at is immutable")(created.applied_at)(updated.applied_at);

    // 3. Attempt to mutate immutable fields by providing them in update body (should be ignored by API)
    const fakeUpdates: IDiscount.IUpdate & Partial<IDiscount> = {
        amount: (updated.amount || 0) - 555,
        notes: "Attempt with immutable fields",
        // @ts-expect-error: forcibly adding illegal keys for test (they should be ignored)
        id: "00000000-0000-0000-0000-000000000abc",
        // @ts-expect-error
        discount_rule_id: "11111111-1111-1111-1111-111111111111",
        // @ts-expect-error
        order_id: "22222222-2222-2222-2222-222222222222",
        // @ts-expect-error
        applied_at: "2021-02-01T12:00:00Z",
    };
    const afterImmutableUpdate: IDiscount = await api.functional.paymentsDiscounts.discounts.putById(connection, {
        id: created.id,
        body: fakeUpdates,
    });
    typia.assert<IDiscount>(afterImmutableUpdate);
    // Immutable fields must match originally created/updated values
    TestValidator.equals("id must remain immutable")(created.id)(afterImmutableUpdate.id);
    TestValidator.equals("discount_rule_id must remain immutable")(created.discount_rule_id)(afterImmutableUpdate.discount_rule_id);
    TestValidator.equals("order_id must remain immutable")(created.order_id)(afterImmutableUpdate.order_id);
    TestValidator.equals("applied_at must remain immutable")(created.applied_at)(afterImmutableUpdate.applied_at);
    // Mutable fields should have updated as normal
    if (typeof fakeUpdates.amount === "number")
        TestValidator.equals("amount should update with valid change")(fakeUpdates.amount)(afterImmutableUpdate.amount);
    if (typeof fakeUpdates.notes !== "undefined")
        TestValidator.equals("notes should update with valid change")(fakeUpdates.notes)(afterImmutableUpdate.notes);

    // 4. Attempt to update a non-existent discount
    await TestValidator.httpError("should fail for non-existent discount")
        (404)
        (async () => {
            await api.functional.paymentsDiscounts.discounts.putById(connection, {
                id: typia.random<string & tags.Format<"uuid">>(),
                body: { notes: "This should not work for non-existent ID" },
            });
        });
}

