import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPageIUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIUser";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import typia from "typia";

export async function test_list_users_with_pagination_and_filtering(connection: api.IConnection): Promise<void> {
  // 1. Retrieve paginated user list with default settings
  const list1 = await api.functional.core.users.patch(connection, { body: {} });
  typia.assert<IPageIUser>(list1);
  TestValidator.predicate("default user list has users")(list1.data.length >= 0);
  TestValidator.predicate("pagination metadata present")(!!list1.pagination && typeof list1.pagination.current === "number");

  // Pick a username from the result for next filtering (if any user exists)
  const filterUser: IUser | undefined = list1.data[0];
  if (filterUser) {
    // 2. Filter by existing username, expect only matching users
    const list2 = await api.functional.core.users.patch(connection, {
      body: { username: filterUser.username },
    });
    typia.assert<IPageIUser>(list2);
    for (const user of list2.data) {
      TestValidator.equals("username filtered")(filterUser.username)(user.username);
    }
  }

  // 3. Invalid pagination: negative page number
  await TestValidator.httpError("negative page")(400)(() =>
    api.functional.core.users.patch(connection, { body: { page: -1 } })
  );

  // 3. Invalid pagination: excessively large limit
  await TestValidator.httpError("excessive limit")(400)(() =>
    api.functional.core.users.patch(connection, { body: { limit: 100000 } })
  );

  // 3. Invalid pagination: zero limit
  await TestValidator.httpError("zero limit")(400)(() =>
    api.functional.core.users.patch(connection, { body: { limit: 0 } })
  );

  // 4. Out-of-bounds page number (way past last page)
  const impossiblePage = list1.pagination.pages + 50;
  const outOfBounds = await api.functional.core.users.patch(connection, { body: { page: impossiblePage } });
  typia.assert<IPageIUser>(outOfBounds);
  TestValidator.equals("out-of-bounds empty list")([] as IUser[])(outOfBounds.data);
  TestValidator.equals("pagination metadata current equals requested")(
    impossiblePage,
  )(outOfBounds.pagination.current);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPageIUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIUser";

/**
 * E2E Test: List users after creating and simulating soft-delete
 */
export async function test_list_users_after_creating_and_deleting_users(connection: api.IConnection): Promise<void> {
  // Step 1: Prepare unique users (same org, role for filtering); use typia.random as base
  const shared_org_id = typia.random<string & typia.tags.Format<"uuid">>();
  const shared_role_id = typia.random<string & typia.tags.Format<"uuid">>();
  const userInputs: IUser.ICreate[] = [0, 1, 2].map((i) => {
    const base = typia.random<IUser.ICreate>();
    return {
      ...base,
      organization_id: shared_org_id,
      role_id: shared_role_id,
      username: `testuser_${Date.now()}_${i}`,
      email: `testuser_${Date.now()}_${i}@example.com`,
    };
  });

  // Step 2: Create users via POST
  const created: IUser[] = [];
  for (const input of userInputs) {
    const res = await api.functional.core.users.post(connection, { body: input });
    typia.assert<IUser>(res);
    created.push(res);
  }

  // Step 3: List users, unfiltered
  const listRes: IPageIUser = await api.functional.core.users.patch(connection, {
    body: {
      organization_id: shared_org_id,
    },
  });
  typia.assert<IPageIUser>(listRes);
  // Confirm all users present by id
  const createdIds = created.map((u) => u.id);
  const returnedIds = listRes.data.map((u) => u.id);
  for (const id of createdIds) {
    TestValidator.predicate(`Created user ${id} present in list!`)(returnedIds.includes(id));
  }

  // Step 4: Soft-delete simulation (skip if no API for soft-delete)
  // --- If a soft-delete API existed, you would call: await api.functional.core.users.delete(connection, { ... })
  // --- As it is not provided, we'll just check 'deleted_at' on results in future steps
  // Step 5: Simulate soft-delete by removing an item from the set used in asserts
  const softDeleted = created[0];
  /*
  // Uncomment and implement when soft-delete API is provided
  await api.functional.core.users.delete(connection, { id: softDeleted.id });
  */
  // Simulate by filtering
  const remaining = created.filter((u) => u.id !== softDeleted.id);

  // Step 6: List after simulated deletion; in a real API, softDeleted should no longer appear
  const afterDeleteList: IPageIUser = await api.functional.core.users.patch(connection, {
    body: {
      organization_id: shared_org_id,
    },
  });
  const afterDeleteIds = afterDeleteList.data.map((u) => u.id);

  TestValidator.predicate("Soft-deleted user should not be returned")(!afterDeleteIds.includes(softDeleted.id));
  for (const u of remaining) {
    TestValidator.predicate(`Remaining user ${u.id} should be present`)(afterDeleteIds.includes(u.id));
  }

  // Step 7: Filtering test by username (should find a remaining user)
  const target = remaining[0];
  const usernameFilter: IPageIUser = await api.functional.core.users.patch(connection, {
    body: {
      username: target.username,
      organization_id: shared_org_id,
    },
  });
  typia.assert<IPageIUser>(usernameFilter);
  TestValidator.predicate("User found by username")(usernameFilter.data.some((u) => u.id === target.id));

  // Filtering by email
  const emailFilter: IPageIUser = await api.functional.core.users.patch(connection, {
    body: {
      email: target.email,
      organization_id: shared_org_id,
    },
  });
  typia.assert<IPageIUser>(emailFilter);
  TestValidator.predicate("User found by email")(emailFilter.data.some((u) => u.id === target.id));
}

import { TestValidator } from "@nestia/e2e";
